import React from "react";
import { useNavigate } from "react-router-dom";

export default function Home() {
  const navigate = useNavigate();

  const handleClick = (path) => {
    navigate(path);
  };

  return (
    <div className="flex flex-col min-h-screen font-sans">
      {/* Navbar */}
      <nav className="flex justify-between items-center px-10 py-5 bg-white shadow-md fixed w-full z-10">
        <div className="text-xl font-bold text-blue-900">
          Bharat PM Internship
        </div>
        <div className="flex space-x-6 text-blue-900 font-medium">
          <button onClick={() => handleClick("/")} className="hover:text-orange-500 transition">Home</button>
          <button onClick={() => handleClick("/find")} className="hover:text-orange-500 transition">Find Internships</button>
          <button onClick={() => handleClick("/industries")} className="hover:text-orange-500 transition">For Industries</button>
          <button onClick={() => handleClick("/login")} className="hover:text-orange-500 transition">Login</button>
        </div>
      </nav>

      {/* Hero Section */}
      <header
        className="flex flex-col items-center justify-center flex-grow text-center px-6 bg-cover bg-center relative"
        style={{
          backgroundImage:
            "url('https://images.unsplash.com/photo-1522075469751-3a6694fb2f61?auto=format&fit=crop&w=1600&q=80')",
        }}
      >
        {/* Dark overlay */}
        <div className="absolute inset-0 bg-black bg-opacity-50"></div>

        <div className="relative z-10 max-w-3xl mt-24">
          <h1 className="text-6xl md:text-7xl font-extrabold text-white mb-6 drop-shadow-lg">
            Bharat PM Internship
          </h1>
          <p className="text-xl md:text-2xl italic text-gray-200 mb-12 drop-shadow-md">
            "Internships are stepping stones to your future. <br />
            Learn, explore, and discover your true potential."
          </p>
          <button
            onClick={() => handleClick("/find")}
            className="px-8 py-3 bg-orange-500 text-white rounded-lg text-lg font-semibold hover:bg-orange-600 shadow-lg transition-all"
          >
            Find Your Dream Internship
          </button>
        </div>
      </header>

      {/* Footer Section */}
      <footer className="bg-blue-900 py-10 text-center text-white mt-auto">
        <div className="flex flex-col items-center">
          <img
            src="https://upload.wikimedia.org/wikipedia/en/0/0f/Smart_India_Hackathon_Logo.png"
            alt="SIH Logo"
            className="w-20 mb-4 animate-bounce"
          />
          <h3 className="text-2xl font-bold">Smart India Hackathon</h3>
          <p className="mt-2 text-gray-200">
            Empowering Innovation â€¢ Driving Nation Building
          </p>
        </div>
      </footer>
    </div>
  );
}
