import React, { useState, useEffect } from "react";

// Categories for dropdown
const categories = ["General", "OBC", "SC", "ST", "Rural", "Aspirational"];

export default function MatchEnginePage() {
  const [formData, setFormData] = useState({
    name: "",
    contact: "",
    college: "",
    skills: "",
    location: "",
    sector: "",
    category: "",
    resume: null,
    pastParticipation: false,
  });

  const [submitted, setSubmitted] = useState(false);
  const [matchedInternships, setMatchedInternships] = useState([]);
  const [internshipsData, setInternshipsData] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch real-time internships from backend
  useEffect(() => {
    fetch("http://localhost:5000/api/internships")
      .then(res => res.json())
      .then(data => {
        setInternshipsData(data);
        setLoading(false);
      })
      .catch(err => {
        console.error("Failed to fetch internships:", err);
        setLoading(false);
      });
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if (type === "checkbox") setFormData({ ...formData, [name]: checked });
    else if (type === "file") setFormData({ ...formData, [name]: files[0] });
    else setFormData({ ...formData, [name]: value });
  };

  // Smart matching algorithm
  const calculateScore = (student, internship) => {
    const studentSkills = student.skills.toLowerCase().split(",").map(s => s.trim());
    const skillMatchCount = internship.skills.filter(skill =>
      studentSkills.includes(skill.toLowerCase())
    ).length;

    let score = skillMatchCount * 20 + Math.random() * 10;

    if (student.pastParticipation) score -= 10;
    if (internship.location.toLowerCase() === student.location.toLowerCase()) score += 5;
    if (["SC","ST","OBC","Rural","Aspirational"].includes(student.category)) score += 10;
    if (internship.appliedCount >= internship.capacity) score -= 100;

    return Math.max(Math.round(score), 0);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const scoredInternships = internshipsData.map(intern => ({
      ...intern,
      score: calculateScore(formData, intern),
    }));

    scoredInternships.sort((a, b) => b.score - a.score);
    const top3 = scoredInternships.slice(0, 3);

    // Increment appliedCount simulation
    top3.forEach(intern => {
      const index = internshipsData.findIndex(i => i.company === intern.company && i.title === intern.title);
      if (index !== -1) internshipsData[index].appliedCount += 1;
    });

    setMatchedInternships(top3);
    setSubmitted(true);
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-pink-50 via-purple-50 to-indigo-50 p-6 flex flex-col md:flex-row font-sans">

      {/* Left: Student Form */}
      <form 
        className="bg-white p-8 rounded-2xl shadow-2xl w-full md:w-1/2 space-y-4"
        onSubmit={handleSubmit}
      >
        <h2 className="text-3xl font-bold text-purple-800 mb-2">Student Registration</h2>
        <p className="text-gray-600 mb-4">Complete your details to discover top internship matches ðŸš€</p>

        <input type="text" name="name" placeholder="Full Name" value={formData.name} onChange={handleChange} className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-purple-400" required />
        <input type="text" name="contact" placeholder="Contact Info" value={formData.contact} onChange={handleChange} className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-purple-400" required />
        <input type="text" name="college" placeholder="College/University" value={formData.college} onChange={handleChange} className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-purple-400" required />
        <input type="text" name="location" placeholder="Preferred Location" value={formData.location} onChange={handleChange} className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-purple-400" required />
        <input type="text" name="sector" placeholder="Sector of Interest" value={formData.sector} onChange={handleChange} className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-purple-400" />
        <input type="text" name="skills" placeholder="Skills (Python, ML, React, etc.)" value={formData.skills} onChange={handleChange} className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-purple-400" required />

        <select name="category" value={formData.category} onChange={handleChange} required className="w-full border p-3 rounded-lg focus:ring-2 focus:ring-purple-400">
          <option value="">Select Category</option>
          {categories.map(cat => <option key={cat} value={cat}>{cat}</option>)}
        </select>

        <label className="block">
          Resume/CV Upload
          <input type="file" name="resume" onChange={handleChange} className="mt-2 w-full" required />
        </label>

        <label className="flex items-center space-x-2">
          <input type="checkbox" name="pastParticipation" checked={formData.pastParticipation} onChange={handleChange} />
          <span>Past Participation in Internship</span>
        </label>

        <button type="submit" className="w-full py-3 bg-purple-800 hover:bg-purple-900 text-white rounded-lg font-bold text-lg transition duration-300">Find My Internships</button>
      </form>

      {/* Right: Banners + Matches */}
      <div className="md:pl-8 mt-6 md:mt-0 w-full md:w-1/2 flex flex-col">
        <div className="grid grid-cols-2 gap-4 mb-6">
          <img src="https://i.postimg.cc/j2C7QGXZ/banner1.jpg" alt="Banner1" className="rounded-xl shadow-lg" />
          <img src="https://i.postimg.cc/RV6t3xq0/banner2-jpg.png" alt="Banner2" className="rounded-xl shadow-lg" />
        </div>

        {!submitted ? (
          <div className="bg-white p-6 rounded-2xl shadow-2xl flex-1 flex flex-col items-center justify-center space-y-4">
            <h2 className="text-2xl font-bold mb-2 text-purple-700">Your Internship Matches</h2>
            <p className="text-gray-500 text-center italic">"Every skill you master is a step closer to your dream career. Fill the form and let opportunities find you!"</p>
            {loading && <p className="text-gray-400 mt-2">Loading internships...</p>}
          </div>
        ) : (
          <div className="bg-purple-900 p-6 rounded-2xl shadow-2xl text-white flex-1 space-y-6">
            <h2 className="text-2xl font-bold text-orange-400">Top Internship Matches</h2>
            {matchedInternships.map((intern, idx) => (
              <div key={idx} className="p-4 bg-purple-800 rounded-xl shadow-lg hover:scale-105 transform transition relative">
                <h3 className="text-xl font-bold">{intern.title} - <span className="text-green-400">{intern.company}</span></h3>
                <p>Location: {intern.location}</p>
                <p>Skills: {intern.skills.join(", ")}</p>
                <div className="mt-2 h-4 w-full bg-purple-600 rounded-full overflow-hidden">
                  <div
                    className="h-full bg-green-400 transition-all duration-1000"
                    style={{ width: `${intern.score}%` }}
                  ></div>
                </div>
                <p className="mt-1 text-yellow-300 font-semibold">Score: {intern.score}</p>
                <a href={intern.url} target="_blank" rel="noopener noreferrer" className="absolute top-2 right-2 text-blue-300 hover:underline">View</a>
              </div>
            ))}
            <p className="mt-4 text-yellow-200 font-semibold text-center">Matches are calculated using skills, location, category & fair allocation.</p>
          </div>
        )}
      </div>
    </div>
  );
}
