import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function StudentRegister() {
  const [step, setStep] = useState(1);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleNext = (e) => {
    e.preventDefault();
    setStep(2);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("You have registered successfully!");
    navigate("/student-login");
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-6">
      <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-md">
        <h2 className="text-2xl font-bold mb-6 text-center">Student Registration</h2>

        {step === 1 && (
          <form onSubmit={handleNext} className="flex flex-col gap-4">
            <input
              type="text"
              placeholder="Full Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
              className="p-3 border rounded-md"
            />
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              className="p-3 border rounded-md"
            />
            <button type="submit" className="bg-blue-900 text-white py-3 rounded-md font-semibold hover:bg-blue-800">
              Next
            </button>
          </form>
        )}

        {step === 2 && (
          <form onSubmit={handleSubmit} className="flex flex-col gap-4">
            <input
              type="password"
              placeholder="Create Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              className="p-3 border rounded-md"
            />
            <button type="submit" className="bg-green-600 text-white py-3 rounded-md font-semibold hover:bg-green-500">
              Submit
            </button>
          </form>
        )}
      </div>
    </div>
  );
}
