import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function StudentLogin() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = () => {
    alert("Login successful!");
    navigate("/student-dashboard"); // navigate to dashboard after login
  };

  return (
    <section className="flex flex-col items-center justify-center py-12 bg-gray-100 min-h-screen">
      <div className="max-w-md w-full bg-white p-8 rounded-lg shadow-md">
        <div className="bg-yellow-100 border-l-4 border-yellow-500 text-yellow-700 p-4 mb-6">
          <p className="text-sm">
            <strong>Important:</strong> We've upgraded our authentication system from May 2025. 
            If you were using 'Sign in with user name', please confirm your sign-up with the same email id again.{" "}
            <span 
              className="underline cursor-pointer text-blue-600" 
              onClick={() => navigate("/student-register")}
            >
              Click here
            </span> to create account with 'Sign up with email'. Users with social login (Gmail or Microsoft) are not affected.
          </p>
        </div>

        <h2 className="text-2xl font-bold text-center mb-4">Sign in with your Email</h2>

        <input
          type="email"
          placeholder="Email Address"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="w-full p-3 mb-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="w-full p-3 mb-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
        />

        <p className="text-sm text-right text-blue-600 cursor-pointer mb-4">Forgot your password?</p>

        <button
          onClick={handleLogin}
          className="w-full bg-blue-600 text-white py-3 rounded-lg font-semibold hover:bg-blue-700 transition-colors"
        >
          Sign In
        </button>

        <div className="flex items-center my-4">
          <hr className="flex-grow border-gray-300" />
          <span className="mx-2 text-gray-400">OR</span>
          <hr className="flex-grow border-gray-300" />
        </div>

        <p className="text-center text-gray-700">
          Don't have an account?{" "}
          <span
            className="text-blue-600 cursor-pointer font-semibold"
            onClick={() => navigate("/student-register")}
          >
            Sign up now
          </span>
        </p>
      </div>
    </section>
  );
}
