import React from "react";
import { useNavigate } from "react-router-dom";

const HeroSection = () => {
  const navigate = useNavigate();
  return (
    <section className="pt-32 text-center relative">
      <h1 className="text-5xl md:text-6xl font-bold text-blue-900 drop-shadow-lg mb-4">
        Bharat Internship
      </h1>
      <p className="text-xl md:text-2xl italic text-gray-700 mb-6 max-w-3xl mx-auto">
        "Internships are stepping stones to your future. Learn, explore, and discover your true potential."
      </p>
      <button
        onClick={() => navigate("/find")}
        className="px-8 py-3 bg-orange-500 text-white rounded-lg font-semibold hover:bg-orange-600 shadow-lg transition"
      >
        Find Your Dream Internship
      </button>

      {/* PM Poster */}
      <div
        className="mt-12 mx-auto h-[400px] w-3/4 bg-cover bg-center rounded-lg shadow-lg"
        style={{ backgroundImage: "url('https://kj1bcdn.b-cdn.net/media/101006/pm-internship-scheme.jpg')" }}
      ></div>

      {/* Horizontal Scrollable Industry Logos */}
      <div className="my-16">
        <h2 className="text-3xl font-bold text-blue-900 mb-6">Our Partner Industries</h2>
        <div className="overflow-x-auto flex items-center px-6 py-6 space-x-8 scrollbar-thin scrollbar-thumb-orange-500 scrollbar-track-gray-200">
          {[
            "https://upload.wikimedia.org/wikipedia/commons/thumb/5/51/IBM_logo.svg/1280px-IBM_logo.svg.png",
            "https://upload.wikimedia.org/wikipedia/commons/thumb/9/96/Microsoft_logo_%282012%29.svg/2560px-Microsoft_logo_%282012%29.svg.png",
            "https://mma.prnewswire.com/media/2441415/Deloitte_Logo.jpg?p=facebook",
            "https://assets.weforum.org/organization/image/tU1CE7gt11tLJEY9Pq-8xJ5qOsXv40ra_0AZ4fE14vo.jpg",
            "https://upload.wikimedia.org/wikipedia/commons/thumb/2/2f/Google_2015_logo.svg/1200px-Google_2015_logo.svg.png",
          ].map((logo, index) => (
            <div key={index} className="flex-shrink-0 w-40 h-24 flex items-center justify-center">
              <img
                src={logo}
                alt={`Company ${index}`}
                className="h-20 object-contain rounded-lg shadow-md hover:scale-110 transition-transform"
              />
            </div>
          ))}
        </div>
      </div>

      {/* Footer */}
      <footer className="relative bg-blue-900 py-10 px-10 text-white mt-12 rounded-t-xl">
        <img
          src="https://upload.wikimedia.org/wikipedia/en/0/0f/Smart_India_Hackathon_Logo.png"
          alt="SIH Logo"
          className="w-20 absolute top-4 right-10"
        />
        <div className="text-center">
          <h3 className="text-2xl font-bold">Smart India Hackathon</h3>
          <p className="mt-2 text-gray-200">Empowering Innovation â€¢ Driving Nation Building</p>
        </div>
      </footer>
    </section>
  );
};

export default HeroSection;
