import React, { useState } from "react";

// Sample internship data
const internshipsData = [
  { title: "ML Intern", company: "Innovate AI", location: "Bengaluru", skills: ["Python","ML"], capacity:5, appliedCount:0 },
  { title: "Data Science Intern", company: "Data Insights", location: "Hyderabad", skills:["Python","SQL"], capacity:3, appliedCount:0 },
  { title: "Frontend Developer", company: "WebWeavers", location: "Remote", skills:["React","CSS"], capacity:4, appliedCount:0 },
  { title: "Backend Developer", company: "CodeFactory", location: "Pune", skills:["Node.js","SQL"], capacity:2, appliedCount:0 },
  { title: "AI Research Intern", company: "NeuralNet Inc", location: "Bengaluru", skills:["Python","ML","TensorFlow"], capacity:3, appliedCount:0 },
];

const categories = ["General", "OBC", "SC", "ST", "Rural", "Aspirational"];

export default function MatchEnginePage() {
  const [formData, setFormData] = useState({
    name: "", contact: "", college: "",
    skills: "", skillsArray: [], skillInput: "",
    location: "", sector: "", category: "",
    resume: null, pastParticipation: false
  });

  const [submitted, setSubmitted] = useState(false);
  const [matchedInternships, setMatchedInternships] = useState([]);
  const [darkMode, setDarkMode] = useState(true);

  // Handle input change
  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if(type==="checkbox") setFormData({...formData, [name]: checked});
    else if(type==="file") setFormData({...formData, [name]: files[0]});
    else setFormData({...formData, [name]: value});
  };

  // Calculate internship match score
  const calculateScore = (student, internship) => {
    const studentSkills = student.skills.toLowerCase().split(",").map(s=>s.trim());
    const skillMatchCount = internship.skills.filter(skill => studentSkills.includes(skill.toLowerCase())).length;
    let score = skillMatchCount*20 + Math.random()*10;
    if(student.pastParticipation) score-=10;
    if(internship.location.toLowerCase()===student.location.toLowerCase()) score+=5;
    if(["SC","ST","OBC","Rural","Aspirational"].includes(student.category)) score+=10;
    if(internship.appliedCount>=internship.capacity) score-=100;
    return Math.max(Math.round(score),0);
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    const scoredInternships = internshipsData.map(intern=>({...intern, score: calculateScore(formData,intern)}));
    scoredInternships.sort((a,b)=>b.score-a.score);
    const top3 = scoredInternships.slice(0,3);
    top3.forEach(intern=>{
      const index = internshipsData.findIndex(i=>i.company===intern.company && i.title===intern.title);
      if(index!==-1) internshipsData[index].appliedCount+=1;
    });
    setMatchedInternships(top3);
    setSubmitted(true);
  };

  return (
    <div className={`${darkMode ? "bg-gray-900 text-white" : "bg-gray-100 text-gray-900"} min-h-screen p-6 flex flex-col md:flex-row gap-6 transition-colors duration-500`}>

      {/* Dark Mode Toggle */}
      <div className="absolute top-5 right-5 flex items-center gap-2">
        <span>{darkMode ? "Dark Mode" : "Light Mode"}</span>
        <button onClick={()=>setDarkMode(!darkMode)} className="w-12 h-6 bg-gray-500 rounded-full relative shadow-inner transition-all duration-300">
          <div className={`w-5 h-5 bg-white rounded-full absolute top-0.5 transition-all duration-300 ${darkMode ? "left-0.5" : "left-6"}`}></div>
        </button>
      </div>

      {/* Student Form */}
      <form 
        onSubmit={handleSubmit}
        className={`${darkMode ? "bg-gray-800/70 border-gray-700" : "bg-white/80 border-gray-200"} backdrop-blur-md border rounded-3xl p-8 w-full md:w-1/2 flex flex-col space-y-5 shadow-lg hover:shadow-2xl transition duration-500`}
      >
        <h2 className="text-3xl font-bold text-center mb-2 neon-text">Student Registration</h2>
        <p className="text-sm text-center text-gray-400">Fill your details to discover top internship matches</p>

        {/* Floating Inputs */}
        {["name","contact","college","location","sector"].map((field,i)=>(
          <div key={i} className="relative">
            <input 
              type="text" name={field} value={formData[field]} onChange={handleChange} placeholder=" " required
              className="peer w-full p-4 rounded-xl bg-gray-700/20 border border-gray-600 text-white placeholder-transparent focus:outline-none focus:ring-2 focus:ring-cyan-400 transition"
            />
            <label className="absolute left-4 top-4 text-gray-400 text-sm peer-placeholder-shown:top-4 peer-placeholder-shown:text-gray-500 peer-placeholder-shown:text-base peer-focus:top-1 peer-focus:text-cyan-400 peer-focus:text-sm transition-all">
              {field.charAt(0).toUpperCase()+field.slice(1).replace(/([A-Z])/g,' $1')}
            </label>
          </div>
        ))}

        {/* Skills Input as Tags */}
        <div>
          <label className="text-gray-400 mb-1 block">Skills (Type & Enter)</label>
          <div className="flex flex-wrap gap-2 p-2 bg-gray-700/20 rounded-xl border border-gray-600">
            {formData.skillsArray.map((skill, idx)=>(
              <span key={idx} className="bg-cyan-500 text-black px-3 py-1 rounded-full flex items-center gap-1 shadow-lg">
                {skill}
                <button type="button" onClick={()=>{
                  const newSkills = formData.skillsArray.filter((s,i)=>i!==idx);
                  setFormData({...formData, skillsArray:newSkills, skills:newSkills.join(", ")});
                }} className="ml-1 font-bold hover:text-red-300">&times;</button>
              </span>
            ))}
            <input
              type="text" value={formData.skillInput} onChange={(e)=>setFormData({...formData, skillInput:e.target.value})}
              onKeyDown={(e)=>{
                if(e.key==="Enter" && formData.skillInput.trim()!==""){
                  e.preventDefault();
                  const newSkillsArray = [...formData.skillsArray, formData.skillInput.trim()];
                  setFormData({...formData, skillsArray:newSkillsArray, skills:newSkillsArray.join(", "), skillInput:""});
                }
              }}
              className="bg-transparent outline-none flex-1 p-2 placeholder-gray-400"
              placeholder="Add skills..."
            />
          </div>
        </div>

        {/* Category */}
        <select name="category" value={formData.category} onChange={handleChange} required
          className="w-full p-4 rounded-xl bg-gray-700/20 border border-gray-600 text-white focus:outline-none focus:ring-2 focus:ring-cyan-400 transition">
          <option value="" disabled>Select Category</option>
          {categories.map(cat=><option key={cat} value={cat}>{cat}</option>)}
        </select>

        {/* Resume */}
        <label className="flex flex-col text-white">
          Upload Resume
          <input type="file" name="resume" onChange={handleChange} className="mt-2 p-2 rounded-xl bg-gray-700/20 border border-gray-600 text-white" required />
        </label>

        {/* Past Participation */}
        <label className="flex items-center space-x-3 cursor-pointer">
          <input type="checkbox" name="pastParticipation" checked={formData.pastParticipation} onChange={handleChange} className="w-5 h-5 accent-cyan-400" />
          <span className="text-white font-medium">Past Participation</span>
        </label>

        {/* Submit */}
        <button type="submit" className="w-full py-4 bg-cyan-500 hover:bg-cyan-600 rounded-2xl font-bold text-black text-lg shadow-lg hover:shadow-cyan-500 transition neon-glow">
          Find My Internships
        </button>
      </form>

      {/* Matches Panel */}
      <div className="w-full md:w-1/2 flex flex-col gap-6">
        {!submitted ? (
          <div className={`${darkMode ? "bg-gray-800/70 border-gray-700" : "bg-white/80 border-gray-200"} backdrop-blur-md border rounded-3xl p-8 flex-1 flex flex-col items-center justify-center text-center text-gray-400 animate-pulse`}>
            <h2 className="text-2xl font-bold mb-2 neon-text">Your Internship Matches</h2>
            <p>Complete the form to see your top 3 matches</p>
          </div>
        ) : (
          <div className="flex flex-col gap-4">
            {matchedInternships.map((intern,idx)=>(
              <div key={idx} className={`${darkMode ? "bg-gray-800/70 border-gray-700" : "bg-white/80 border-gray-200"} backdrop-blur-md border rounded-2xl p-6 text-white shadow-lg transform hover:scale-105 transition duration-500`}>
                <h3 className="text-xl font-bold neon-text">{intern.title} <span className="text-cyan-400">- {intern.company}</span></h3>
                <p className="mt-1">Location: {intern.location}</p>
                <p>Skills: {intern.skills.join(", ")}</p>

                {/* Animated Score Bar */}
                <div className="mt-2 w-full h-4 bg-gray-600 rounded-full overflow-hidden">
                  <div className="h-4 bg-cyan-400 rounded-full transition-all duration-1000" style={{width:`${intern.score}%`}}></div>
                </div>
                <p className="text-right mt-1 font-semibold">{intern.score}% Match</p>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Neon Glow Styles */}
      <style>{`
        .neon-text {
          text-shadow: 0 0 5px #0ff, 0 0 10px #0ff, 0 0 20px #0ff;
        }
        .neon-glow {
          box-shadow: 0 0 5px #0ff, 0 0 10px #0ff, 0 0 20px #0ff;
        }
      `}</style>
    </div>
  );
}
