import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function SignInPage() {
  const [isSignUp, setIsSignUp] = useState(false); // toggle between Sign In / Sign Up
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = () => {
    alert("Login successful!");
    navigate("/student-dashboard");
  };

  const handleSignUp = () => {
    if (password !== confirmPassword) {
      alert("Passwords do not match!");
      return;
    }
    alert(`Sign Up successful! Welcome, ${name}`);
    navigate("/student-dashboard");
  };

  return (
    <section className="flex flex-col items-center justify-center py-12 bg-gray-100 min-h-screen">
      <div className="max-w-md w-full bg-white p-8 rounded-lg shadow-md">
        {/* Notice */}


        {!isSignUp ? (
          <>
            <h2 className="text-2xl font-bold text-center mb-4">Sign in with your Email</h2>
            <input
              type="email"
              placeholder="Email Address"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full p-3 mb-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full p-3 mb-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <p className="text-sm text-right text-blue-600 cursor-pointer mb-4">Forgot your password?</p>
            <button
              onClick={handleLogin}
              className="w-full bg-blue-600 text-white py-3 rounded-lg font-semibold hover:bg-blue-700 transition-colors"
            >
              Sign In
            </button>
            <div className="flex items-center my-4">
              <hr className="flex-grow border-gray-300" />
              <span className="mx-2 text-gray-400">OR</span>
              <hr className="flex-grow border-gray-300" />
            </div>
            <p className="text-center text-gray-700">
              Don't have an account?{" "}
              <span
                className="text-blue-600 cursor-pointer font-semibold"
                onClick={() => setIsSignUp(true)}
              >
                Sign up now
              </span>
            </p>
          </>
        ) : (
          <>
            <h2 className="text-2xl font-bold text-center mb-4">Sign Up</h2>
            <input
              type="text"
              placeholder="Full Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="w-full p-3 mb-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <input
              type="email"
              placeholder="Email Address"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full p-3 mb-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full p-3 mb-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <input
              type="password"
              placeholder="Confirm Password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              className="w-full p-3 mb-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <button
              onClick={handleSignUp}
              className="w-full bg-green-600 text-white py-3 rounded-lg font-semibold hover:bg-green-700 transition-colors"
            >
              Sign Up
            </button>
            <p className="text-center text-gray-700 mt-4">
              Already have an account?{" "}
              <span
                className="text-blue-600 cursor-pointer font-semibold"
                onClick={() => setIsSignUp(false)}
              >
                Sign in
              </span>
            </p>
          </>
        )}
      </div>
    </section>
  );
}
