// src/pages/MatchEngine.jsx
import React, { useState, useEffect } from "react";

const categories = ["General", "OBC", "SC", "ST", "Rural", "Aspirational"];

export default function MatchEngine() {
  const [formData, setFormData] = useState({
    name: "",
    contact: "",
    college: "",
    location: "",
    sector: "",
    category: "",
    skillsArray: [],
    skillInput: "",
    resume: null,
    pastParticipation: false,
  });

  const [submitted, setSubmitted] = useState(false);
  const [internshipsData, setInternshipsData] = useState([]);
  const [matchedInternships, setMatchedInternships] = useState([]);

  // Fetch real-time internships from backend
  useEffect(() => {
    const fetchInternships = async () => {
      try {
        const unstopRes = await fetch("http://localhost:5000/api/internships/unstop");
        const naukriRes = await fetch("http://localhost:5000/api/internships/naukri");
        const unstopData = await unstopRes.json();
        const naukriData = await naukriRes.json();
        setInternshipsData([...unstopData, ...naukriData]);
      } catch (err) {
        console.error("Error fetching internships:", err);
      }
    };
    fetchInternships();
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if (type === "checkbox") setFormData({ ...formData, [name]: checked });
    else if (type === "file") setFormData({ ...formData, [name]: files[0] });
    else setFormData({ ...formData, [name]: value });
  };

  const calculateScore = (student, internship) => {
    const studentSkills = student.skillsArray.map((s) => s.toLowerCase());
    const skillMatchCount = internship.skills.filter((skill) =>
      studentSkills.includes(skill.toLowerCase())
    ).length;

    let score = skillMatchCount * 20 + Math.random() * 10;
    if (student.pastParticipation) score -= 10;
    if (internship.location.toLowerCase() === student.location.toLowerCase())
      score += 5;
    if (["SC", "ST", "OBC", "Rural", "Aspirational"].includes(student.category))
      score += 10;
    if (internship.appliedCount >= internship.capacity) score -= 100;

    return Math.max(Math.round(score), 0);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (internshipsData.length === 0) return;

    const scoredInternships = internshipsData.map((intern) => ({
      ...intern,
      score: calculateScore(formData, intern),
    }));
    scoredInternships.sort((a, b) => b.score - a.score);

    const top3 = scoredInternships.slice(0, 3);
    top3.forEach((intern) => {
      const index = internshipsData.findIndex(
        (i) => i.title === intern.title && i.company === intern.company
      );
      if (index !== -1) internshipsData[index].appliedCount += 1;
    });

    setMatchedInternships(top3);
    setSubmitted(true);
  };

  return (
    <div className="min-h-screen flex flex-col md:flex-row p-6 gap-6">
      {/* Form */}
      <form
        onSubmit={handleSubmit}
        className="flex flex-col gap-4 p-6 rounded-2xl bg-white/70 backdrop-blur-md shadow-lg md:w-1/2"
      >
        <h2 className="text-2xl font-bold text-center text-purple-700">
          Student Registration
        </h2>
        <p className="text-sm text-gray-700 text-center">
          Fill your details to discover top internship matches
        </p>

        {["name", "contact", "college", "location", "sector"].map((field) => (
          <div key={field} className="flex flex-col">
            <label className="font-semibold">{field.charAt(0).toUpperCase() + field.slice(1)}</label>
            <input
              type="text"
              name={field}
              value={formData[field]}
              onChange={handleChange}
              placeholder={`Enter your ${field}`}
              className="p-2 rounded-xl border border-gray-300 focus:ring-2 focus:ring-purple-300 focus:outline-none"
              required
            />
          </div>
        ))}

        {/* Skills */}
        <div className="flex flex-col">
          <label className="font-semibold">Skills (Type & Enter)</label>
          <div className="flex flex-wrap gap-2 p-2 border border-gray-300 rounded-xl">
            {formData.skillsArray.map((skill, idx) => (
              <span key={idx} className="bg-purple-300 text-gray-900 px-3 py-1 rounded-full flex items-center gap-1">
                {skill}
                <button
                  type="button"
                  onClick={() =>
                    setFormData({
                      ...formData,
                      skillsArray: formData.skillsArray.filter((_, i) => i !== idx),
                    })
                  }
                >
                  &times;
                </button>
              </span>
            ))}
            <input
              type="text"
              value={formData.skillInput}
              onChange={(e) => setFormData({ ...formData, skillInput: e.target.value })}
              onKeyDown={(e) => {
                if (e.key === "Enter" && formData.skillInput.trim() !== "") {
                  e.preventDefault();
                  setFormData({
                    ...formData,
                    skillsArray: [...formData.skillsArray, formData.skillInput.trim()],
                    skillInput: "",
                  });
                }
              }}
              className="flex-1 outline-none bg-transparent"
              placeholder="Add skills..."
            />
          </div>
        </div>

        {/* Category */}
        <div className="flex flex-col">
          <label className="font-semibold">Category</label>
          <select
            name="category"
            value={formData.category}
            onChange={handleChange}
            className="p-2 rounded-xl border border-gray-300"
            required
          >
            <option value="" disabled>
              Select Category
            </option>
            {categories.map((cat) => (
              <option key={cat} value={cat}>
                {cat}
              </option>
            ))}
          </select>
        </div>

        {/* Resume Upload */}
        <label className="flex flex-col">
          Upload Resume
          <input type="file" name="resume" onChange={handleChange} className="mt-2 border p-2 rounded-xl" required />
        </label>

        {/* Past Participation */}
        <label className="flex items-center gap-2">
          <input type="checkbox" name="pastParticipation" checked={formData.pastParticipation} onChange={handleChange} />
          Past Participation
        </label>

        <button
          type="submit"
          className="bg-purple-400 text-white py-3 rounded-xl hover:bg-purple-500 transition"
        >
          Find My Internships
        </button>
      </form>

      {/* Matches */}
      <div className="md:w-1/2 flex flex-col gap-4">
        {!submitted ? (
          <div className="flex-1 flex items-center justify-center text-gray-500 text-center">
            Fill the form and discover your top internships!
          </div>
        ) : (
          matchedInternships.map((intern, idx) => (
            <div
              key={idx}
              className="p-4 rounded-xl bg-white/70 shadow-md flex flex-col gap-2"
            >
              <h3 className="text-lg font-bold">
                {intern.title} - <span className="text-purple-500">{intern.company}</span>
              </h3>
              <p>Location: {intern.location}</p>
              <p>Skills: {intern.skills.join(", ")}</p>
              <p className="text-right font-semibold">{intern.score}% Match</p>
              <div className="flex gap-2 mt-2">
                <a
                  href={intern.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="flex-1 bg-green-400 py-2 text-white text-center rounded-xl hover:bg-green-500 transition"
                >
                  View
                </a>
                <a
                  href={intern.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="flex-1 bg-blue-400 py-2 text-white text-center rounded-xl hover:bg-blue-500 transition"
                >
                  Apply
                </a>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
