import React, { useState, useEffect } from "react";

const categories = ["General", "OBC", "SC", "ST", "Rural", "Aspirational"];

export default function MatchEnginePage() {
  const [formData, setFormData] = useState({
    name: "", contact: "", college: "",
    skillsArray: [], skillInput: "",
    location: "", sector: "", category: "",
    resume: null, pastParticipation: false
  });

  const [submitted, setSubmitted] = useState(false);
  const [matchedInternships, setMatchedInternships] = useState([]);
  const [internshipsData, setInternshipsData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("http://localhost:5000/api/internships")
      .then(res => res.json())
      .then(data => {
        setInternshipsData(data);
        setLoading(false);
      })
      .catch(err => {
        console.log(err);
        setLoading(false);
      });
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if(type==="checkbox") setFormData({...formData, [name]: checked});
    else if(type==="file") setFormData({...formData, [name]: files[0]});
    else setFormData({...formData, [name]: value});
  };

  const calculateScore = (student, internship) => {
    const studentSkills = student.skillsArray.map(s=>s.toLowerCase());
    const skillMatchCount = internship.skills.filter(skill => studentSkills.includes(skill.toLowerCase())).length;
    let score = skillMatchCount*20 + Math.random()*10;
    if(student.pastParticipation) score-=10;
    if(internship.location.toLowerCase()===student.location.toLowerCase()) score+=5;
    if(["SC","ST","OBC","Rural","Aspirational"].includes(student.category)) score+=10;
    if(internship.appliedCount>=internship.capacity) score-=100;
    return Math.max(Math.round(score),0);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const scoredInternships = internshipsData.map(intern=>({...intern, score: calculateScore(formData,intern)}));
    scoredInternships.sort((a,b)=>b.score-a.score);
    const top3 = scoredInternships.slice(0,3);
    setMatchedInternships(top3);
    setSubmitted(true);
  };

  return (
    <div className="min-h-screen flex flex-col md:flex-row p-6 bg-gradient-to-r from-pink-50 via-purple-50 to-indigo-50 gap-6">

      {/* Form */}
      <form onSubmit={handleSubmit} className="flex-1 bg-white p-6 rounded-3xl shadow space-y-4">
        <h2 className="text-3xl font-bold text-purple-700">Student Registration</h2>
        <p>Complete your details to discover top internship matches ðŸš€</p>

        {["name","contact","college","location","sector"].map((f,i)=>(
          <input key={i} type="text" name={f} placeholder={f} value={formData[f]} onChange={handleChange} className="w-full p-3 border rounded-lg"/>
        ))}

        {/* Skills */}
        <div>
          <label>Skills (Type & Enter)</label>
          <div className="flex flex-wrap gap-2 p-2 border rounded-lg">
            {formData.skillsArray.map((s,i)=>(
              <span key={i} className="bg-purple-200 px-2 py-1 rounded-full">{s}</span>
            ))}
            <input type="text" value={formData.skillInput} onChange={(e)=>setFormData({...formData, skillInput:e.target.value})}
              onKeyDown={(e)=>{if(e.key==="Enter"){e.preventDefault(); setFormData({...formData, skillsArray:[...formData.skillsArray, formData.skillInput], skillInput:""})}}}
              placeholder="Add skills..." className="flex-1 p-1 outline-none"/>
          </div>
        </div>

        <select name="category" value={formData.category} onChange={handleChange} className="w-full p-3 border rounded-lg">
          <option value="">Select Category</option>
          {categories.map(c=><option key={c} value={c}>{c}</option>)}
        </select>

        <input type="file" name="resume" onChange={handleChange}/>
        <label>
          <input type="checkbox" name="pastParticipation" checked={formData.pastParticipation} onChange={handleChange}/>
          Past Participation
        </label>

        <button type="submit" className="w-full bg-purple-700 text-white p-3 rounded-lg">Find My Internships</button>
      </form>

      {/* Matches */}
      <div className="flex-1 flex flex-col gap-4">
        {submitted ? (
          matchedInternships.map((intern,i)=>(
            <div key={i} className="p-4 bg-white rounded-xl shadow">
              <h3>{intern.title} - {intern.company}</h3>
              <p>{intern.location}</p>
              <p>Skills: {intern.skills.join(", ")}</p>
              <p>Score: {intern.score}%</p>
              <div className="flex gap-4 mt-2">
                <a href={intern.url} target="_blank" rel="noopener noreferrer" className="text-blue-500">View</a>
                <a href={intern.url} target="_blank" rel="noopener noreferrer" className="text-green-500">Apply</a>
              </div>
            </div>
          ))
        ) : (
          <p className="text-gray-500 italic">Top 3 internships will appear here after form submission.</p>
        )}
      </div>
    </div>
  );
}
