import React from "react";

// Working online images
const pmPoster = "https://upload.wikimedia.org/wikipedia/commons/5/5c/PM_Modi_in_New_Delhi.jpg";
const companyLogos = [
  "https://upload.wikimedia.org/wikipedia/commons/4/44/IBM_logo.svg",
  "https://upload.wikimedia.org/wikipedia/commons/4/44/Microsoft_logo.svg",
  "https://upload.wikimedia.org/wikipedia/commons/3/33/Deloitte_Logo.svg",
  "https://upload.wikimedia.org/wikipedia/commons/2/2f/Google_2015_logo.svg",
  "https://upload.wikimedia.org/wikipedia/commons/5/53/Infosys_logo.svg"
];
const sihLogo = "https://upload.wikimedia.org/wikipedia/commons/f/f5/Smart_India_Hackathon_Logo.png";

const Home = ({ setCurrentPage }) => {
  return (
    <div className="flex flex-col min-h-screen font-sans">

      {/* Navbar */}
      <nav className="flex justify-between items-center px-10 py-5 bg-white shadow-md fixed w-full z-10">
        <div className="text-2xl font-bold text-blue-900">Bharat Internship</div>
        <div className="flex space-x-8 text-blue-900 font-semibold">
          <button onClick={() => setCurrentPage("home")} className="hover:text-orange-500 transition">Home</button>
          <button onClick={() => setCurrentPage("find")} className="hover:text-orange-500 transition">Find Internships</button>
          <button className="hover:text-orange-500 transition">For Industries</button>
          <button className="hover:text-orange-500 transition">Login</button>
        </div>
      </nav>

      {/* Hero Section */}
      <header
        className="flex flex-col items-center justify-center text-center px-6 relative mt-28 py-20 bg-cover bg-center rounded-lg"
        style={{ backgroundImage: `url(${pmPoster})` }}
      >
        <div className="absolute inset-0 bg-black bg-opacity-50 rounded-lg"></div>
        <div className="relative z-10 flex flex-col items-center justify-center text-white">
          <h1 className="text-5xl font-extrabold mb-4 drop-shadow-lg">Bharat PM Internship</h1>
          <p className="text-xl italic max-w-3xl mb-6 drop-shadow-lg">
            "Internships are stepping stones to your future.<br />
            Learn, explore, and discover your true potential."
          </p>
          <button 
            onClick={() => setCurrentPage("find")}
            className="px-8 py-3 bg-orange-500 text-white rounded-lg text-lg font-semibold hover:bg-orange-600 shadow-lg transition-all"
          >
            Find Your Dream Internship
          </button>
        </div>
      </header>

      {/* Partner Company Logos */}
      <section className="py-10 bg-white">
        <h2 className="text-xl font-semibold text-center mb-4">Our Partner Industries</h2>
        <div className="flex overflow-x-auto space-x-4 px-6 py-4">
          {companyLogos.map((logo, idx) => (
            <div
              key={idx}
              className="flex-shrink-0 w-20 h-20 flex items-center justify-center p-2 rounded-lg hover:scale-110 transition-transform duration-300 bg-gray-50 border border-gray-200"
            >
              <img src={logo} alt={`Company ${idx}`} className="max-h-12 max-w-16 object-contain"/>
            </div>
          ))}
        </div>
      </section>

      {/* SIH Footer */}
      <footer className="bg-blue-900 py-10 text-center text-white mt-auto">
        <div className="flex flex-col items-center">
          <img
            src={sihLogo}
            alt="SIH Logo"
            className="w-20 mb-4 animate-bounce"
          />
          <h3 className="text-2xl font-bold">Smart India Hackathon</h3>
          <p className="mt-2 text-gray-200">Empowering Innovation â€¢ Driving Nation Building</p>
        </div>
      </footer>
    </div>
  );
};

export default Home;
