              <h2 className="text-2xl font-bold mb-2">ðŸš€ Your Internship Journey Awaits!</h2>
              <p className="italic">"Every skill you master is a step closer to your dream career. Fill the form and let opportunities find you!"</p>
            </div>
          ) : (
            matchedInternships.map((intern, idx) => (
              <div key={idx} className="bg-white rounded-2xl p-6 shadow hover:shadow-lg transition transform hover:scale-105">
                <h3 className="text-xl font-bold text-blue-700">{intern.title} <span className="text-blue-500">- {intern.company}</span></h3>
                <p className="mt-1">Location: {intern.location}</p>
                <p>Skills: {intern.skills.join(", ")}</p>
                <div className="mt-2 w-full h-4 bg-gray-200 rounded-full overflow-hidden">
                  <div className="h-4 bg-blue-400 rounded-full transition-all duration-1000" style={{ width: `${intern.score}%` }}></div>
                </div>
                <p className="text-right mt-1 font-semibold">{intern.score}% Match</p>

                <div className="mt-4 flex gap-4">
                  <a href={intern.url} target="_blank" rel="noopener noreferrer"
                    className="flex-1 py-2 bg-green-500 hover:bg-green-600 text-white rounded-xl text-center font-semibold transition">
                    View
                  </a>
                  <a href={intern.url} target="_blank" rel="noopener noreferrer"
                    className="flex-1 py-2 bg-blue-500 hover:bg-blue-600 text-white rounded-xl text-center font-semibold transition">
                    Apply
                  </a>
                </div>
              </div>
            ))
          )}

