import React, { useState, useEffect } from "react";

// Example RSS-to-JSON feed URL (replace with actual Internshala RSS feed)
const FEED_URL = "https://api.rss2json.com/v1/api.json?rss_url=https://internshala.com/internships/rss";

const categories = ["General", "OBC", "SC", "ST", "Rural", "Aspirational"];

export default function MatchEnginePage() {
  const [internshipsData, setInternshipsData] = useState([]);
  const [formData, setFormData] = useState({
    name: "", contact: "", college: "",
    skillsArray: [], skillInput: "",
    location: "", sector: "", category: "",
    resume: null, pastParticipation: false
  });
  const [submitted, setSubmitted] = useState(false);
  const [matchedInternships, setMatchedInternships] = useState([]);

  // Fetch internships from Internshala RSS feed
  useEffect(() => {
    fetch(FEED_URL)
      .then(res => res.json())
      .then(data => {
        // Map RSS feed items to internship structure
        const internships = data.items.map(item => ({
          title: item.title || "Internship",
          company: item.author || "Unknown",
          location: item.categories?.[0] || "Remote",
          skills: ["Skill1", "Skill2"], // replace with parsing if available
          capacity: 5,
          appliedCount: 0,
          url: item.link
        }));
        setInternshipsData(internships);
      })
      .catch(err => console.error("Failed to fetch internships:", err));
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if (type === "checkbox") setFormData({ ...formData, [name]: checked });
    else if (type === "file") setFormData({ ...formData, [name]: files[0] });
    else setFormData({ ...formData, [name]: value });
  };

  const calculateScore = (student, internship) => {
    const studentSkills = student.skillsArray.map(s => s.toLowerCase());
    const skillMatchCount = internship.skills.filter(skill => studentSkills.includes(skill.toLowerCase())).length;
    let score = skillMatchCount * 20 + Math.random() * 10;
    if (student.pastParticipation) score -= 10;
    if (internship.location.toLowerCase() === student.location.toLowerCase()) score += 5;
    if (["SC", "ST", "OBC", "Rural", "Aspirational"].includes(student.category)) score += 10;
    if (internship.appliedCount >= internship.capacity) score -= 100;
    return Math.max(Math.round(score), 0);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const scoredInternships = internshipsData.map(intern => ({
      ...intern,
      score: calculateScore(formData, intern)
    }));
    scoredInternships.sort((a, b) => b.score - a.score);
    const top3 = scoredInternships.slice(0, 3);
    top3.forEach(intern => {
      const index = internshipsData.findIndex(i => i.url === intern.url);
      if (index !== -1) internshipsData[index].appliedCount += 1;
    });
    setMatchedInternships(top3);
    setSubmitted(true);
  };

  return (
    <div className="min-h-screen p-6 bg-gradient-to-r from-purple-100 to-blue-100">
      <h1 className="text-4xl font-bold text-center text-purple-700 mb-6">Internship Match Engine</h1>
      <div className="flex flex-col md:flex-row gap-6">

        {/* Registration Form */}
        <form onSubmit={handleSubmit} className="bg-white p-6 rounded-2xl shadow-md flex-1 flex flex-col gap-4">
          <h2 className="text-2xl font-bold text-purple-700 text-center">Student Registration</h2>
          {["name", "contact", "college", "location", "sector"].map((field, i) => (
            <div key={i} className="flex flex-col">
              <label className="font-semibold">{field.charAt(0).toUpperCase() + field.slice(1)}</label>
              <input type="text" name={field} value={formData[field]} onChange={handleChange}
                className="p-2 rounded-lg border border-gray-300" placeholder={`Enter your ${field}`} required />
            </div>
          ))}

          {/* Skills Input */}
          <div className="flex flex-col">
            <label className="font-semibold">Skills (Type & Enter)</label>
            <div className="flex flex-wrap gap-2 p-2 border border-gray-300 rounded-lg">
              {formData.skillsArray.map((skill, idx) => (
                <span key={idx} className="bg-purple-300 text-gray-900 px-3 py-1 rounded-full flex items-center gap-1">
                  {skill}
                  <button type="button" onClick={() => {
                    const newSkills = formData.skillsArray.filter((s, i) => i !== idx);
                    setFormData({ ...formData, skillsArray: newSkills });
                  }}>&times;</button>
                </span>
              ))}
              <input
                type="text"
                value={formData.skillInput}
                onChange={(e) => setFormData({ ...formData, skillInput: e.target.value })}
                onKeyDown={(e) => {
                  if (e.key === "Enter" && formData.skillInput.trim() !== "") {
                    e.preventDefault();
                    setFormData({
                      ...formData,
                      skillsArray: [...formData.skillsArray, formData.skillInput.trim()],
                      skillInput: ""
                    });
                  }
                }}
                placeholder="Add skills..."
                className="flex-1 outline-none p-1"
              />
            </div>
          </div>

          {/* Category */}
          <select name="category" value={formData.category} onChange={handleChange} required
            className="p-2 border border-gray-300 rounded-lg">
            <option value="" disabled>Select Category</option>
            {categories.map(cat => <option key={cat} value={cat}>{cat}</option>)}
          </select>

          <label className="flex flex-col">
            Upload Resume
            <input type="file" name="resume" onChange={handleChange} className="mt-2 p-2 border rounded-lg" required />
          </label>

          <label className="flex items-center gap-2">
            <input type="checkbox" name="pastParticipation" checked={formData.pastParticipation} onChange={handleChange} />
            Past Participation
          </label>

          <button type="submit" className="bg-purple-400 hover:bg-purple-500 text-white py-2 rounded-xl font-bold">Find Internships</button>
        </form>

        {/* Matched Internships */}
        <div className="flex-1 flex flex-col gap-4">
          {!submitted ? (
            <div className="bg-white p-6 rounded-2xl shadow-md flex-1 flex items-center justify-center text-gray-500">
              Fill the form to discover top internship matches!
            </div>
          ) : (
            matchedInternships.map((intern, idx) => (
              <div key={idx} className="bg-white p-4 rounded-xl shadow-md">
                <h3 className="font-bold text-purple-700">{intern.title} - {intern.company}</h3>
                <p>Location: {intern.location}</p>
                <p>Skills: {intern.skills.join(", ")}</p>
                <div className="mt-2 w-full h-4 bg-gray-300 rounded-full overflow-hidden">
                  <div className="h-4 bg-purple-300 rounded-full" style={{ width: `${intern.score}%` }}></div>
                </div>
                <p className="text-right font-semibold">{intern.score}% Match</p>
                <div className="flex gap-2 mt-2">
                  <a href={intern.url} target="_blank" rel="noopener noreferrer" className="flex-1 bg-green-400 hover:bg-green-500 text-white py-1 rounded-lg text-center font-semibold">View</a>
                  <a href={intern.url} target="_blank" rel="noopener noreferrer" className="flex-1 bg-blue-400 hover:bg-blue-500 text-white py-1 rounded-lg text-center font-semibold">Apply</a>
                </div>
              </div>
            ))
          )}
        </div>

      </div>
    </div>
  );
}
