import React from "react";

const HeroSection = () => {
  return (
    <section className="text-center py-20 bg-gray-50">
      <h2 className="text-4xl font-bold text-blue-900 mb-4">
        Connecting India’s Talent with Tomorrow’s Opportunities
      </h2>
      <p className="text-lg mb-6">
        Our AI-powered engine intelligently matches aspiring interns with premier opportunities,
        fostering skill development and nation-building.
      </p>
      <button className="bg-orange-500 text-white px-6 py-3 rounded-lg text-lg font-semibold">
        Find Your Dream Internship
      </button>
    </section>
  );
};

export default HeroSection;
