import React, { useState } from "react";

// Sample internship data (simulate backend/AI engine)
const internshipsData = [
  { title: "ML Intern", company: "Innovate AI", location: "Bengaluru", skills: ["Python","ML"], capacity:5, appliedCount:0 },
  { title: "Data Science Intern", company: "Data Insights", location: "Hyderabad", skills:["Python","SQL"], capacity:3, appliedCount:0 },
  { title: "Frontend Developer", company: "WebWeavers", location: "Remote", skills:["React","CSS"], capacity:4, appliedCount:0 },
  { title: "Backend Developer", company: "CodeFactory", location: "Pune", skills:["Node.js","SQL"], capacity:2, appliedCount:0 },
  { title: "AI Research Intern", company: "NeuralNet Inc", location: "Bengaluru", skills:["Python","ML","TensorFlow"], capacity:3, appliedCount:0 },
];

const categories = ["General", "OBC", "SC", "ST", "Rural", "Aspirational"];

export default function MatchEnginePage() {
  const [formData, setFormData] = useState({
    name: "", contact: "", college: "", skills: "", location: "", sector: "", category: "", resume: null, pastParticipation: false,
  });

  const [submitted, setSubmitted] = useState(false);
  const [matchedInternships, setMatchedInternships] = useState([]);

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if (type === "checkbox") setFormData({ ...formData, [name]: checked });
    else if (type === "file") setFormData({ ...formData, [name]: files[0] });
    else setFormData({ ...formData, [name]: value });
  };

  const calculateScore = (student, internship) => {
    const studentSkills = student.skills.toLowerCase().split(",").map(s => s.trim());
    const skillMatchCount = internship.skills.filter(skill =>
      studentSkills.includes(skill.toLowerCase())
    ).length;
    let score = skillMatchCount*20 + Math.random()*10;
    if (student.pastParticipation) score -= 10;
    if (internship.location.toLowerCase() === student.location.toLowerCase()) score += 5;
    if (["SC","ST","OBC","Rural","Aspirational"].includes(student.category)) score += 10;
    if (internship.appliedCount >= internship.capacity) score -= 100;
    return Math.max(Math.round(score),0);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const scoredInternships = internshipsData.map(intern => ({ ...intern, score: calculateScore(formData, intern) }));
    scoredInternships.sort((a,b)=>b.score-a.score);
    const top3 = scoredInternships.slice(0,3);
    top3.forEach(intern=>{
      const index = internshipsData.findIndex(i=>i.company===intern.company && i.title===intern.title);
      if(index!==-1) internshipsData[index].appliedCount+=1;
    });
    setMatchedInternships(top3);
    setSubmitted(true);
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-indigo-900 via-purple-900 to-pink-900 p-6 flex flex-col md:flex-row items-center justify-center gap-6">

      {/* Futuristic Student Form */}
      <form 
        onSubmit={handleSubmit}
        className="bg-white/10 backdrop-blur-md border border-white/20 rounded-3xl p-8 w-full md:w-1/2 flex flex-col space-y-5 shadow-xl hover:shadow-2xl transition duration-500"
      >
        <h2 className="text-4xl font-extrabold text-white text-center mb-4 tracking-wide">Student Registration</h2>
        <p className="text-white/70 text-sm text-center mb-6">Fill your details to find the best internship matches</p>

        {/* Floating Inputs */}
        {["name","contact","college","skills","location","sector"].map((field,i)=>(
          <div key={i} className="relative">
            <input 
              type="text"
              name={field}
              value={formData[field]}
              onChange={handleChange}
              placeholder=" "
              required
              className="peer w-full p-4 rounded-xl bg-white/20 border border-white/30 text-white placeholder-transparent focus:outline-none focus:ring-2 focus:ring-purple-400 transition"
            />
            <label className="absolute left-4 top-4 text-white/50 text-sm peer-placeholder-shown:top-4 peer-placeholder-shown:text-white/50 peer-placeholder-shown:text-base peer-focus:top-1 peer-focus:text-white/70 peer-focus:text-sm transition-all">
              {field.charAt(0).toUpperCase()+field.slice(1).replace(/([A-Z])/g,' $1')}
            </label>
          </div>
        ))}

        {/* Category Select */}
        <select
          name="category"
          value={formData.category}
          onChange={handleChange}
          required
          className="w-full p-4 rounded-xl bg-white/20 border border-white/30 text-white focus:outline-none focus:ring-2 focus:ring-purple-400 transition"
        >
          <option value="" disabled>Select Category</option>
          {categories.map(cat=> <option key={cat} value={cat}>{cat}</option>)}
        </select>

        {/* Resume Upload */}
        <label className="flex flex-col text-white">
          Upload Resume
          <input type="file" name="resume" onChange={handleChange} className="mt-2 p-2 rounded-xl text-black bg-white/30 border border-white/30" required />
        </label>

        {/* Checkbox */}
        <label className="flex items-center space-x-3 cursor-pointer">
          <input type="checkbox" name="pastParticipation" checked={formData.pastParticipation} onChange={handleChange} className="w-5 h-5 accent-purple-400" />
          <span className="text-white font-medium">Past Participation in Internship</span>
        </label>

        {/* Submit Button */}
        <button type="submit" className="w-full py-4 bg-gradient-to-r from-purple-500 to-pink-500 hover:from-pink-500 hover:to-purple-500 rounded-2xl font-bold text-white text-lg tracking-wide shadow-lg hover:shadow-xl transition duration-300">
          Find My Internships
        </button>
      </form>

      {/* Futuristic Matches Panel */}
      <div className="w-full md:w-1/2 flex flex-col gap-6">
        {!submitted ? (
          <div className="bg-white/10 backdrop-blur-md border border-white/20 rounded-3xl p-8 flex-1 flex flex-col items-center justify-center text-center text-white/70 animate-pulse">
            <h2 className="text-3xl font-bold mb-2">Your Internship Matches</h2>
            <p>Complete the form to see your top 3 matches</p>
          </div>
        ) : (
          <div className="flex flex-col gap-4">
            {matchedInternships.map((intern,idx)=>(
              <div key={idx} className="bg-white/10 backdrop-blur-md border border-white/20 rounded-2xl p-6 text-white shadow-xl transform hover:scale-105 transition duration-500">
                <h3 className="text-2xl font-bold">{intern.title} <span className="text-purple-400">- {intern.company}</span></h3>
                <p className="mt-1">Location: {intern.location}</p>
                <p>Skills: {intern.skills.join(", ")}</p>
                <p className="mt-2 font-semibold text-yellow-300">Score: {intern.score}</p>
              </div>
            ))}
          </div>
        )}
      </div>

    </div>
  )
}
