import React from "react";

// Alternative PM Poster (from Wikimedia)
const pmPoster = "https://upload.wikimedia.org/wikipedia/commons/1/1b/Narendra_Modi_2019.jpg";

// Company domains for Clearbit logos (online)
const companyDomains = [
  "ibm.com", "microsoft.com", "infosys.com", "wipro.com", "accenture.com",
  "tcs.com", "oracle.com", "google.com", "amazon.com", "facebook.com",
  "apple.com", "cognizant.com", "adobe.com", "hpe.com", "deloitte.com",
  "siemens.com", "sap.com", "hp.com", "intel.com", "qualcomm.com",
  "sony.com", "samsung.com", "twitter.com", "linkedin.com", "uber.com",
  "airbnb.com", "netflix.com", "paypal.com", "stripe.com", "salesforce.com"
];

// Generate Clearbit logo URLs
const companyLogos = companyDomains.map(domain => `https://logo.clearbit.com/${domain}`);

// Alternative SIH logo (official Wikimedia)
const sihLogo = "https://upload.wikimedia.org/wikipedia/commons/4/4d/Smart_India_Hackathon_Logo.png";

export default function Home() {
  return (
    <div className="flex flex-col min-h-screen font-sans">

      {/* Navbar */}
      <nav className="flex justify-between items-center px-10 py-5 bg-white shadow-md fixed w-full z-10">
        <div className="text-2xl font-bold text-blue-900">Bharat Internship</div>
        <div className="flex space-x-8 text-blue-900 font-semibold">
          <button className="hover:text-orange-500 transition">Home</button>
          <button className="hover:text-orange-500 transition">Find Internships</button>
          <button className="hover:text-orange-500 transition">For Industries</button>
          <button className="hover:text-orange-500 transition">Login</button>
        </div>
      </nav>

      {/* Hero Section with PM Poster */}
      <header
        className="flex flex-col items-center justify-center text-center px-6 bg-cover bg-center relative mt-20"
        style={{ backgroundImage: `url(${pmPoster})`, minHeight: "500px" }}
      >
        <div className="absolute inset-0 bg-black bg-opacity-50 rounded-lg"></div>
        <div className="relative z-10 flex flex-col items-center justify-center text-white">
          <h1 className="text-5xl font-extrabold mb-4 drop-shadow-lg">
            Bharat PM Internship
          </h1>
          <p className="text-xl italic max-w-3xl mb-6 drop-shadow-lg">
            "Internships are stepping stones to your future.<br />
            Learn, explore, and discover your true potential."
          </p>
          <button className="px-8 py-3 bg-orange-500 text-white rounded-lg text-lg font-semibold hover:bg-orange-600 shadow-lg transition-all">
            Find Your Dream Internship
          </button>
        </div>
      </header>

      {/* Partner Company Logos (Horizontal Scroll) */}
      <section className="py-10 bg-white">
        <h2 className="text-xl font-semibold text-center mb-4">Our Partner Industries</h2>
        <div className="flex overflow-x-auto space-x-4 px-6 py-2">
          {companyLogos.map((logo, idx) => (
            <div
              key={idx}
              className="flex-shrink-0 w-24 h-24 flex items-center justify-center p-2 rounded-lg hover:scale-110 transition-transform duration-300 bg-gray-50 border border-gray-200"
            >
              <img src={logo} alt={`Company ${idx}`} className="max-h-16 max-w-20 object-contain" />
            </div>
          ))}
        </div>
      </section>

      {/* SIH Footer */}
      <footer className="bg-blue-900 py-10 text-center text-white mt-auto">
        <div className="flex flex-col items-center">
          <img
            src={sihLogo}
            alt="SIH Logo"
            className="w-20 mb-4 animate-bounce"
          />
          <h3 className="text-2xl font-bold">Smart India Hackathon</h3>
          <p className="mt-2 text-gray-200">
            Empowering Innovation â€¢ Driving Nation Building
          </p>
        </div>
      </footer>
    </div>
  );
}
