import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

// Images
const pmPoster = "https://i.postimg.cc/dQWnSN8s/pm-photo.jpg";
const sihLogo = "https://i.postimg.cc/MGRrC0zs/sih-logo.jpg";
const pmPhoto = "https://i.postimg.cc/HLc0Ws32/Whats-App-Image-2025-09-22-at-7-22-21-PM.jpg";

// Company domains
const companyDomains = [
  "ibm.com","microsoft.com","infosys.com","wipro.com","accenture.com",
  "tcs.com","oracle.com","google.com","amazon.com","facebook.com",
  "apple.com","cognizant.com","adobe.com","hpe.com","deloitte.com",
  "siemens.com","sap.com","hp.com","intel.com","qualcomm.com",
  "sony.com","samsung.com","twitter.com","linkedin.com","uber.com",
  "airbnb.com","netflix.com","paypal.com","stripe.com","salesforce.com",
  "zoom.us","spotify.com","slack.com","dropbox.com","github.com",
  "atlassian.com","tesla.com","nvidia.com","amd.com","redhat.com",
  "salesforce.com","square.com","oracle.com","mozilla.org","snap.com",
  "coinbase.com","binance.com","expedia.com","booking.com","airtel.in"
];

const companyLogos = companyDomains.map(domain => `https://logo.clearbit.com/${domain}`);

export default function Home() {
  const [menuOpen, setMenuOpen] = useState(false);
  const [dashOpen, setDashOpen] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = () => {
    alert("Login successful!");
    navigate("/student-dashboard");
  };

  return (
    <div className="flex flex-col min-h-screen font-sans relative">

      {/* Navbar */}
      <nav className="flex justify-between items-center px-10 py-5 bg-white shadow-md fixed w-full z-50">
        <div className="text-2xl font-bold text-blue-900">Bharat Internship</div>

        <div className="flex items-center gap-4">
          <button
            onClick={() => navigate("/student-login")}
            className="px-4 py-2 bg-blue-900 text-white rounded-lg font-semibold hover:bg-blue-800"
          >
            Login
          </button>
          <button
            onClick={() => navigate("/student-register")}
            className="px-4 py-2 bg-yellow-400 text-blue-900 rounded-lg font-semibold hover:bg-yellow-500"
          >
            Register
          </button>

          <button
            onClick={() => setMenuOpen(!menuOpen)}
            className="ml-4 p-2 rounded-md bg-blue-700 hover:bg-blue-600 text-white"
          >
            ☰
          </button>
        </div>

        {menuOpen && (
          <div className="absolute top-20 right-10 bg-blue-800 text-white flex flex-col p-4 space-y-3 rounded-md shadow-lg z-50">
            <Link to="/student-login" className="hover:underline">Student Login</Link>
            <Link to="/student-register" className="hover:underline">Student Register</Link>

            <button
              onClick={() => setDashOpen(!dashOpen)}
              className="text-left hover:underline font-semibold"
            >
              Dashboards ▼
            </button>

            {dashOpen && (
              <div className="flex flex-col pl-4 mt-2 space-y-2">
                <Link to="/student-dashboard" className="hover:underline">Student Dashboard</Link>
                <Link to="/industry-dashboard" className="hover:underline">Industry Dashboard</Link>
                <Link to="/admin-dashboard" className="hover:underline">Admin Dashboard</Link>
              </div>
            )}
          </div>
        )}
      </nav>

      {/* Hero Section */}
      <header
        className="flex flex-col items-center justify-center text-center px-6 bg-cover bg-center relative mt-24"
        style={{ backgroundImage: `url(${pmPoster})`, minHeight: "500px" }}
      >
        <div className="absolute inset-0 bg-black bg-opacity-50 rounded-lg"></div>
        <div className="relative z-10 flex flex-col items-center justify-center text-white">
          <h1 className="text-5xl font-extrabold mb-4 drop-shadow-lg">Bharat PM Internship</h1>
          <p className="text-xl italic max-w-3xl mb-6 drop-shadow-lg">
            "Internships are stepping stones to your future. Learn, explore, and discover your true potential."
          </p>
          <button
            onClick={() => navigate("/student-register")}
            className="px-8 py-3 bg-orange-500 text-white rounded-lg text-lg font-semibold hover:bg-orange-600 shadow-lg transition-all"
          >
            Find Your Dream Internship
          </button>
        </div>
      </header>

      {/* Sign In / Notice Section */}
      <section className="flex flex-col items-center justify-center py-12 bg-gray-100">
        <div className="max-w-md w-full bg-white p-8 rounded-lg shadow-md">
          <div className="bg-yellow-100 border-l-4 border-yellow-500 text-yellow-700 p-4 mb-6">
            <p className="text-sm">
              <strong>Important:</strong> We've upgraded our authentication system from May 2025. 
              If you were using 'Sign in with user name', please confirm your sign-up with the same email id again. 
              <span className="underline cursor-pointer text-blue-600" onClick={() => navigate("/student-register")}>
                Click here
              </span> to create account with 'Sign up with email'. Users with social login (Gmail or Microsoft) are not affected.
            </p>
          </div>

          <h2 className="text-2xl font-bold text-center mb-4">Sign in with your Email</h2>

          <input
            type="email"
            placeholder="Email Address"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full p-3 mb-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full p-3 mb-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <p className="text-sm text-right text-blue-600 cursor-pointer mb-4">Forgot your password?</p>

          <button
            onClick={handleLogin}
            className="w-full bg-blue-600 text-white py-3 rounded-lg font-semibold hover:bg-blue-700 transition-colors"
          >
            Sign In
          </button>

          <div className="flex items-center my-4">
            <hr className="flex-grow border-gray-300" />
            <span className="mx-2 text-gray-400">OR</span>
            <hr className="flex-grow border-gray-300" />
          </div>

          <p className="text-center text-gray-700">
            Don't have an account?{" "}
            <span
              className="text-blue-600 cursor-pointer font-semibold"
              onClick={() => navigate("/student-register")}
            >
              Sign up now
            </span>
          </p>
        </div>
      </section>

      {/* Partner Companies Section */}
      {/* ... keep the same scrolling company logos code ... */}

      {/* PM Section */}
      {/* ... keep the same PM section code ... */}

      {/* Footer */}
      {/* ... keep the same Footer code ... */}

    </div>
  );
}
