import React, { useState } from "react";
import axios from "axios";
import ProfileForm from "../components/ProfileForm";
import MatchesList from "../components/MatchesList";

export default function MatchEnginePage() {
  const [internships, setInternships] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (formData) => {
    setIsLoading(true);
    try {
      const res = await axios.get("http://localhost:5000/internships", {
        params: {
          skill: formData.skills,
          location: formData.location,
          past: formData.pastParticipation || 0
        }
      });
      setInternships(res.data);
    } catch (err) {
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="flex flex-col md:flex-row p-4 sm:p-8 bg-gray-50 min-h-screen">
      <ProfileForm onSubmit={handleSubmit} />
      <MatchesList internships={internships} isLoading={isLoading} />
    </div>
  );
}
