import React, { useState } from "react";

export default function StudentRegisterPage() {
  const [formData, setFormData] = useState({
    name: "",
    skills: [],
    resume: null,
    location: "",
    category: "",
    past_participation: false,
  });

  const categories = ["General","OBC","SC","ST","Rural","Aspirational District"];

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if (type === "checkbox") setFormData({ ...formData, [name]: checked });
    else if (type === "file") setFormData({ ...formData, resume: files[0] });
    else setFormData({ ...formData, [name]: value });
  };

  const handleSkillAdd = (e) => {
    if (e.key === "Enter" && e.target.value.trim() !== "") {
      e.preventDefault();
      setFormData({ ...formData, skills: [...formData.skills, e.target.value.trim()] });
      e.target.value = "";
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append("name", formData.name);
    data.append("skills", JSON.stringify(formData.skills));
    data.append("resume", formData.resume);
    data.append("location", formData.location);
    data.append("category", formData.category);
    data.append("past_participation", formData.past_participation);

    try {
      const res = await fetch("http://localhost:5000/api/students/register", {
        method: "POST",
        body: data,
      });
      if (res.ok) alert("Student registered successfully!");
      else alert("Error registering student");
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="max-w-xl mx-auto p-6 bg-white rounded-2xl shadow-lg">
      <h2 className="text-2xl font-bold text-gray-800 mb-4">Student Registration</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input type="text" name="name" placeholder="Full Name" value={formData.name} onChange={handleChange} required className="w-full border p-2 rounded-lg"/>
        <div>
          <label className="block text-gray-700 mb-1">Skills</label>
          <input type="text" placeholder="Type a skill and press Enter" onKeyDown={handleSkillAdd} className="w-full border p-2 rounded-lg"/>
          <div className="flex flex-wrap mt-2 gap-2">
            {formData.skills.map((skill, idx) => (
              <span key={idx} className="bg-blue-100 text-blue-700 px-3 py-1 rounded-full text-sm">{skill}</span>
            ))}
          </div>
        </div>
        <input type="file" name="resume" accept=".pdf,.doc,.docx" onChange={handleChange} required className="w-full border p-2 rounded-lg"/>
        <input type="text" name="location" placeholder="Location" value={formData.location} onChange={handleChange} required className="w-full border p-2 rounded-lg"/>
        <select name="category" value={formData.category} onChange={handleChange} required className="w-full border p-2 rounded-lg">
          <option value="">Select Category</option>
          {categories.map((cat) => <option key={cat} value={cat}>{cat}</option>)}
        </select>
        <label className="flex items-center space-x-2">
          <input type="checkbox" name="past_participation" checked={formData.past_participation} onChange={handleChange}/>
          <span>Have you participated before?</span>
        </label>
        <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700 transition">Register</button>
      </form>
    </div>
  );
}
