// src/pages/Home.jsx
import React from "react";

const companyLogos = [
  "https://upload.wikimedia.org/wikipedia/commons/5/51/IBM_logo.svg",
  "https://upload.wikimedia.org/wikipedia/commons/4/44/Microsoft_logo.svg",
  "https://upload.wikimedia.org/wikipedia/commons/4/4f/Deloitte.svg",
  "https://upload.wikimedia.org/wikipedia/commons/c/cd/Walmart_logo.svg",
  "https://upload.wikimedia.org/wikipedia/commons/2/2f/Google_2015_logo.svg"
];

const Home = ({ setCurrentPage }) => {
  return (
    <div>
      {/* Hero Section */}
      <section className="text-center py-20 bg-gray-50">
        <h1 className="text-5xl font-bold text-blue-900 mb-4">
          Connecting India’s Talent with Tomorrow’s Opportunities
        </h1>
        <p className="text-lg text-gray-600 max-w-3xl mx-auto mb-8">
          Our AI-powered engine intelligently matches aspiring interns with premier opportunities, fostering skill development and nation-building.
        </p>
        <button
          onClick={() => setCurrentPage("find")}
          className="bg-orange-500 hover:bg-orange-600 text-white px-8 py-3 rounded-lg text-lg font-semibold shadow-lg transition transform hover:scale-105 duration-300"
        >
          Find Your Dream Internship
        </button>
      </section>

      {/* Quote Section */}
      <section className="text-center py-16">
        <p className="text-xl italic text-gray-700 max-w-2xl mx-auto">
          "Internships are stepping stones to your future. Learn, explore, and discover your true potential."
        </p>
      </section>

      {/* Scrollable Company Logos */}
      <section className="py-10 bg-white">
        <h2 className="text-2xl font-bold text-center mb-6">Our Partner Industries</h2>
        <div className="flex overflow-x-auto space-x-8 px-8">
          {companyLogos.map((logo, idx) => (
            <div key={idx} className="flex-shrink-0 w-32 h-20 flex items-center justify-center p-2 border rounded-lg shadow hover:scale-105 transition transform duration-300 bg-gray-50">
              <img src={logo} alt={`Company ${idx}`} className="max-h-12 object-contain" />
            </div>
          ))}
        </div>
      </section>

      {/* SIH Section */}
      <section className="bg-blue-900 py-8 text-white text-center">
        <div className="flex items-center justify-center space-x-4 mb-4">
          <img
            src="https://upload.wikimedia.org/wikipedia/commons/0/08/Smart_India_Hackathon_logo.png"
            alt="SIH Logo"
            className="h-12"
          />
          <h3 className="text-2xl font-bold">Smart India Hackathon</h3>
        </div>
        <p className="text-lg">Empowering Innovation • Driving Nation Building</p>
      </section>
    </div>
  );
};

export default Home;
