import React, { useState } from "react";

const ProfileForm = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    name: "",
    college: "",
    field: "",
    year: "",
    skills: "",
    location: "",
    sector: ""
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <form
      className="bg-white p-6 rounded shadow w-80"
      onSubmit={(e) => {
        e.preventDefault();
        onSubmit(formData);
      }}
    >
      <h3 className="text-lg font-bold mb-4">Personal & Academic Details</h3>

      <input name="name" placeholder="Full Name" onChange={handleChange} className="w-full mb-3 p-2 border rounded" />
      <input name="college" placeholder="College/University" onChange={handleChange} className="w-full mb-3 p-2 border rounded" />
      <input name="field" placeholder="Field of Study" onChange={handleChange} className="w-full mb-3 p-2 border rounded" />
      <input name="year" placeholder="Graduation Year" onChange={handleChange} className="w-full mb-3 p-2 border rounded" />

      <h3 className="text-lg font-bold mt-4 mb-2">Skills & Preferences</h3>
      <input name="skills" placeholder="e.g. Python, React, ML" onChange={handleChange} className="w-full mb-3 p-2 border rounded" />
      <input name="location" placeholder="Preferred Location" onChange={handleChange} className="w-full mb-3 p-2 border rounded" />
      <input name="sector" placeholder="Sector of Interest" onChange={handleChange} className="w-full mb-3 p-2 border rounded" />

      <button type="submit" className="w-full bg-blue-900 text-white py-2 rounded">
        Find My Internship
      </button>
    </form>
  );
};

export default ProfileForm;
