import React, { useState } from "react";

const ProfileForm = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    name: "",
    college: "",
    field: "",
    year: "",
    skills: "",
    location: "",
    sector: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <form
      className="bg-white p-6 rounded-xl shadow-lg w-full md:w-96 flex-shrink-0"
      onSubmit={(e) => {
        e.preventDefault();
        onSubmit(formData);
      }}
    >
      <h3 className="text-xl font-bold text-gray-800 mb-4">Personal & Academic Details</h3>
      <input name="name" placeholder="Full Name" value={formData.name} onChange={handleChange} className="w-full mb-3 p-3 border rounded" />
      <input name="college" placeholder="College" value={formData.college} onChange={handleChange} className="w-full mb-3 p-3 border rounded" />
      <input name="field" placeholder="Field of Study" value={formData.field} onChange={handleChange} className="w-full mb-3 p-3 border rounded" />
      <input name="year" placeholder="Graduation Year" value={formData.year} onChange={handleChange} className="w-full mb-3 p-3 border rounded" />
      <h3 className="text-xl font-bold text-gray-800 mb-4 mt-4">Skills & Preferences</h3>
      <input name="skills" placeholder="Skills" value={formData.skills} onChange={handleChange} className="w-full mb-3 p-3 border rounded" />
      <input name="location" placeholder="Location" value={formData.location} onChange={handleChange} className="w-full mb-3 p-3 border rounded" />
      <input name="sector" placeholder="Sector" value={formData.sector} onChange={handleChange} className="w-full mb-3 p-3 border rounded" />
      <button type="submit" className="w-full bg-blue-800 text-white py-3 mt-4 rounded-lg">Find My Internship</button>
    </form>
  );
};

export default function MatchEnginePage() {
  const [submitted, setSubmitted] = useState(null);

  const handleSubmit = async (formData) => {
    try {
      const response = await fetch("http://localhost:5000/api/students", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      const result = await response.json();
      if (result.success) setSubmitted(result.student);
      else alert("Submission failed");
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div className="flex flex-col md:flex-row p-6">
      <ProfileForm onSubmit={handleSubmit} />
      <div className="flex-1 md:pl-6 mt-6 md:mt-0">
        {submitted ? (
          <div className="bg-green-100 p-6 rounded-lg shadow">
            <h3 className="text-lg font-bold mb-2">Submission Successful!</h3>
            <pre>{JSON.stringify(submitted, null, 2)}</pre>
          </div>
        ) : (
          <p>Fill out the form to find your internship!</p>
        )}
      </div>
    </div>
  );
}
