import React, { useState, useEffect } from "react";

// Sample internship data
const internshipsData = [
  { title: "ML Intern", company: "Innovate AI", location: "Bengaluru", skills: ["Python","ML"], capacity:5, appliedCount:0 },
  { title: "Data Science Intern", company: "Data Insights", location: "Hyderabad", skills:["Python","SQL"], capacity:3, appliedCount:0 },
  { title: "Frontend Developer", company: "WebWeavers", location: "Remote", skills:["React","CSS"], capacity:4, appliedCount:0 },
  { title: "Backend Developer", company: "CodeFactory", location: "Pune", skills:["Node.js","SQL"], capacity:2, appliedCount:0 },
  { title: "AI Research Intern", company: "NeuralNet Inc", location: "Bengaluru", skills:["Python","ML","TensorFlow"], capacity:3, appliedCount:0 },
];

const categories = ["General", "OBC", "SC", "ST", "Rural", "Aspirational"];

export default function MatchEnginePage() {
  const [formData, setFormData] = useState({
    name: "", contact: "", college: "",
    skills: "", skillsArray: [], skillInput: "",
    location: "", sector: "", category: "",
    resume: null, pastParticipation: false
  });

  const [submitted, setSubmitted] = useState(false);
  const [matchedInternships, setMatchedInternships] = useState([]);

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if(type==="checkbox") setFormData({...formData, [name]: checked});
    else if(type==="file") setFormData({...formData, [name]: files[0]});
    else setFormData({...formData, [name]: value});
  };

  const calculateScore = (student, internship) => {
    const studentSkills = student.skills.toLowerCase().split(",").map(s=>s.trim());
    const skillMatchCount = internship.skills.filter(skill => studentSkills.includes(skill.toLowerCase())).length;
    let score = skillMatchCount*20 + Math.random()*10;
    if(student.pastParticipation) score-=10;
    if(internship.location.toLowerCase()===student.location.toLowerCase()) score+=5;
    if(["SC","ST","OBC","Rural","Aspirational"].includes(student.category)) score+=10;
    if(internship.appliedCount>=internship.capacity) score-=100;
    return Math.max(Math.round(score),0);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const scoredInternships = internshipsData.map(intern=>({...intern, score: calculateScore(formData,intern)}));
    scoredInternships.sort((a,b)=>b.score-a.score);
    const top3 = scoredInternships.slice(0,3);
    top3.forEach(intern=>{
      const index = internshipsData.findIndex(i=>i.company===intern.company && i.title===intern.title);
      if(index!==-1) internshipsData[index].appliedCount+=1;
    });
    setMatchedInternships(top3);
    setSubmitted(true);
  };

  // Particle background
  useEffect(() => {
    const canvas = document.getElementById("particle-canvas");
    const ctx = canvas.getContext("2d");
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const particles = [];
    for(let i=0; i<80; i++){
      particles.push({
        x: Math.random()*canvas.width,
        y: Math.random()*canvas.height,
        radius: Math.random()*3 + 1,
        dx: (Math.random()-0.5)*0.5,
        dy: (Math.random()-0.5)*0.5
      });
    }

    function animate(){
      ctx.clearRect(0,0,canvas.width,canvas.height);
      particles.forEach(p=>{
        ctx.beginPath();
        ctx.arc(p.x, p.y, p.radius, 0, Math.PI*2);
        ctx.fillStyle="rgba(255,255,255,0.6)";
        ctx.fill();
        p.x += p.dx;
        p.y += p.dy;
        if(p.x<0||p.x>canvas.width) p.dx*=-1;
        if(p.y<0||p.y>canvas.height) p.dy*=-1;
      });
      requestAnimationFrame(animate);
    }
    animate();
  }, []);

  return (
    <div className="relative min-h-screen">
      <canvas id="particle-canvas" className="absolute top-0 left-0 w-full h-full z-0"></canvas>
      <div className="relative z-10 flex flex-col md:flex-row gap-6 p-6"
           style={{ background: "linear-gradient(135deg, #c1f0f6, #e0c3fc, #fcd3f9)" }}>

        {/* Student Form */}
        <form 
          onSubmit={handleSubmit}
          className="backdrop-blur-md bg-white/60 border border-white/30 rounded-3xl p-8 w-full md:w-1/2 flex flex-col space-y-5 shadow-xl hover:shadow-2xl transition duration-500 relative"
        >
          <h2 className="text-3xl font-bold text-center mb-2 text-purple-700">Student Registration</h2>
          <p className="text-sm text-center text-gray-600">Complete your details to discover top internship matches</p>

          {["name","contact","college","location","sector"].map((field,i)=>(
            <div key={i} className="relative">
              <input 
                type="text" name={field} value={formData[field]} onChange={handleChange} placeholder=" " required
                className="peer w-full p-4 rounded-xl bg-white/50 border border-gray-300 text-gray-800 placeholder-transparent focus:outline-none focus:ring-2 focus:ring-purple-300 transition"
              />
              <label className="absolute left-4 top-4 text-gray-500 text-sm peer-placeholder-shown:top-4 peer-placeholder-shown:text-gray-400 peer-placeholder-shown:text-base peer-focus:top-1 peer-focus:text-purple-700 peer-focus:text-sm transition-all">
                {field.charAt(0).toUpperCase()+field.slice(1).replace(/([A-Z])/g,' $1')}
              </label>
            </div>
          ))}

          {/* Skills Tags */}
          <div>
            <label className="text-gray-700 mb-1 block">Skills (Type & Enter)</label>
            <div className="flex flex-wrap gap-2 p-2 bg-white/50 rounded-xl border border-gray-300">
              {formData.skillsArray.map((skill, idx)=>(
                <span key={idx} className="bg-purple-300 text-gray-900 px-3 py-1 rounded-full flex items-center gap-1 shadow">
                  {skill}
                  <button type="button" onClick={()=>{
                    const newSkills = formData.skillsArray.filter((s,i)=>i!==idx);
                    setFormData({...formData, skillsArray:newSkills, skills:newSkills.join(", ")});
                  }} className="ml-1 font-bold hover:text-red-500">&times;</button>
                </span>
              ))}
              <input
                type="text" value={formData.skillInput} onChange={(e)=>setFormData({...formData, skillInput:e.target.value})}
                onKeyDown={(e)=>{
                  if(e.key==="Enter" && formData.skillInput.trim()!==""){
                    e.preventDefault();
                    const newSkillsArray = [...formData.skillsArray, formData.skillInput.trim()];
                    setFormData({...formData, skillsArray:newSkillsArray, skills:newSkillsArray.join(", "), skillInput:""});
                  }
                }}
                className="bg-transparent outline-none flex-1 p-2 placeholder-gray-400"
                placeholder="Add skills..."
              />
            </div>
          </div>

          {/* Category */}
          <select name="category" value={formData.category} onChange={handleChange} required
            className="w-full p-4 rounded-xl bg-white/50 border border-gray-300 text-gray-800 focus:outline-none focus:ring-2 focus:ring-purple-300 transition">
            <option value="" disabled>Select Category</option>
            {categories.map(cat=><option key={cat} value={cat}>{cat}</option>)}
          </select>

          {/* Resume */}
          <label className="flex flex-col text-gray-700">
            Upload Resume
            <input type="file" name="resume" onChange={handleChange} className="mt-2 p-2 rounded-xl bg-white/50 border border-gray-300 text-gray-800" required />
          </label>

          {/* Past Participation */}
          <label className="flex items-center space-x-3 cursor-pointer text-gray-700">
            <input type="checkbox" name="pastParticipation" checked={formData.pastParticipation} onChange={handleChange} className="w-5 h-5 accent-purple-300" />
            <span>Past Participation</span>
          </label>

          {/* Submit */}
          <button type="submit" className="w-full py-4 bg-purple-300 hover:bg-purple-400 rounded-2xl font-bold text-gray-900 text-lg shadow transition">
            Find My Internships
          </button>

          {/* Online Banners */}
          <div className="absolute top-0 right-0 w-full h-full pointer-events-none overflow-hidden">
            <img 
              src="https://i.postimg.cc/j2C7QGXZ/banner1.jpg" 
              alt="banner1" 
              className="absolute top-4 right-4 w-40 rounded-xl opacity-80 shadow-lg animate-float" 
            />
            <img 
              src="https://i.postimg.cc/RV6t3xq0/banner2-jpg.png" 
              alt="banner2" 
              className="absolute top-60 right-10 w-36 rounded-xl opacity-80 shadow-lg animate-float-slow" 
            />
          </div>
        </form>

        {/* Matches Panel */}
        <div className="w-full md:w-1/2 flex flex-col gap-6 relative">
          {!submitted ? (
            <div className="backdrop-blur-md bg-white/50 border border-white/30 rounded-3xl p-8 flex-1 flex flex-col items-center justify-center text-center text-gray-500 animate-pulse shadow relative z-10">
              <h2 className="text-2xl font-bold mb-2 text-purple-700 animate-pulse">ðŸš€ Your Internship Journey Awaits!</h2>
              <p className="text-gray-700 italic">
                "Every skill you master is a step closer to your dream career. Fill the form and let opportunities find you!"
              </p>
              <div className="mt-4 w-20 h-1 bg-purple-300 rounded-full mx-auto animate-bounce"></div>
            </div>
          ) : (
            <div className="flex flex-col gap-4 relative z-10">
              {matchedInternships.map((intern, idx) => (
                <div key={idx} className="backdrop-blur-md bg-white/60 border border-white/30 rounded-2xl p-6 shadow transform hover:scale-105 transition duration-500">
                  <h3 className="text-xl font-bold text-purple-700">{intern.title} <span className="text-purple-500">- {intern.company}</span></h3>
                  <p className="mt-1 text-gray-800">Location: {intern.location}</p>
                  <p className="text-gray-800">Skills: {intern.skills.join(", ")}</p>

                  {/* Score Bar */}
                  <div className="mt-2 w-full h-4 bg-gray-300 rounded-full overflow-hidden">
                    <div className="h-4 bg-purple-300 rounded-full transition-all duration-1000" style={{ width: `${intern.score}%` }}></div>
                  </div>
                  <p className="text-right mt-1 font-semibold text-gray-700">{intern.score}% Match</p>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
