import React, { useState } from 'react';

// --- COMPONENTS ---

const Navbar = ({ setCurrentPage }) => {
  return (
    <nav className="bg-white text-gray-800 px-6 sm:px-8 py-4 flex justify-between items-center shadow-md sticky top-0 z-50">
      <div className="flex items-center space-x-2">
        <img src="https://upload.wikimedia.org/wikipedia/commons/5/55/Emblem_of_India.svg" alt="Emblem of India" className="h-8 w-8" />
        <h1 className="text-xl font-bold text-blue-900">Bharat Internship</h1>
      </div>
      <div className="hidden md:flex items-center space-x-6">
        <button onClick={() => setCurrentPage('home')} className="hover:text-blue-700 font-semibold transition duration-300">Home</button>
        <button onClick={() => setCurrentPage('find')} className="hover:text-blue-700 font-semibold transition duration-300">Find Internship</button>
        <button className="hover:text-blue-700 font-semibold transition duration-300">For Industries</button>
        <button className="bg-blue-800 hover:bg-blue-900 text-white px-4 py-2 rounded-lg font-semibold shadow-sm transition duration-300">Login</button>
      </div>
    </nav>
  );
};

const HeroSection = ({ setCurrentPage }) => (
  <section className="text-center py-20 px-4 bg-gray-50">
    <h2 className="text-4xl md:text-5xl font-extrabold text-blue-900 mb-4 leading-tight">
      Connecting India’s Talent with Tomorrow’s Opportunities
    </h2>
    <p className="text-lg text-gray-600 max-w-3xl mx-auto mb-8">
      Our AI-powered engine intelligently matches aspiring interns with premier opportunities,
      fostering skill development and nation-building.
    </p>
    <button
      onClick={() => setCurrentPage('find')}
      className="bg-orange-500 hover:bg-orange-600 text-white px-8 py-3 rounded-lg text-lg font-semibold shadow-lg transition transform hover:scale-105 duration-300"
    >
      Find Your Dream Internship
    </button>
  </section>
);

const ProfileForm = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    name: "",
    college: "",
    field: "",
    year: "",
    skills: "",
    location: "",
    sector: ""
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <form
      className="bg-white p-6 rounded-xl shadow-lg w-full md:w-80 lg:w-96 flex-shrink-0"
      onSubmit={(e) => {
        e.preventDefault();
