import React from "react";

const InternshipCard = ({ title, company, location, skills, match }) => {
  return (
    <div className="border rounded-lg p-4 shadow mb-4 bg-white">
      <h3 className="text-xl font-bold">{title}</h3>
      <p className="text-gray-600">{company} â€¢ {location}</p>
      <div className="flex flex-wrap mt-2">
        {skills.map((skill, i) => (
          <span key={i} className="bg-gray-200 px-2 py-1 text-sm rounded mr-2 mb-2">{skill}</span>
        ))}
      </div>
      <div className="flex justify-between items-center mt-3">
        <span className="text-red-500 font-semibold">{match}% Match</span>
        <button className="bg-green-500 text-white px-4 py-2 rounded">View & Apply</button>
      </div>
    </div>
  );
};

export default InternshipCard;
