import React, { useState, useEffect } from "react";

// Sample internship data
const internshipsData = [
  { id: 1, title: "ML Intern", company: "Innovate AI", location: "Bengaluru", skills: ["Python","ML"], capacity:5, appliedCount:0, url:"https://unstop.com/internship/ml-intern" },
  { id: 2, title: "Data Science Intern", company: "Data Insights", location: "Hyderabad", skills:["Python","SQL"], capacity:3, appliedCount:0, url:"https://unstop.com/internship/data-science" },
  { id: 3, title: "Frontend Developer", company: "WebWeavers", location: "Remote", skills:["React","CSS"], capacity:4, appliedCount:0, url:"https://unstop.com/internship/frontend" },
  { id: 4, title: "Backend Developer", company: "CodeFactory", location: "Pune", skills:["Node.js","SQL"], capacity:2, appliedCount:0, url:"https://unstop.com/internship/backend" },
  { id: 5, title: "AI Research Intern", company: "NeuralNet Inc", location: "Bengaluru", skills:["Python","ML","TensorFlow"], capacity:3, appliedCount:0, url:"https://unstop.com/internship/ai-research" },
];

const categories = ["General", "OBC", "SC", "ST", "Rural", "Aspirational"];

export default function StudentDashboard() {
  const [formData, setFormData] = useState({
    name: "Ambika Srivalli",
    contact: "",
    college: "",
    skillsArray: ["Python","React"],
    location: "Bengaluru",
    sector: "",
    category: "General",
  });

  const [appliedInternships, setAppliedInternships] = useState([]);

  // Calculate match score
  const calculateScore = (student, internship) => {
    const studentSkills = student.skillsArray.map(s => s.toLowerCase());
    const skillMatchCount = internship.skills.filter(skill => studentSkills.includes(skill.toLowerCase())).length;
    const score = Math.min(Math.round((skillMatchCount / internship.skills.length) * 100), 100);
    return score;
  };

  const handleApply = (internship) => {
    // Check if already applied
    const alreadyApplied = appliedInternships.find(i => i.id === internship.id);
    if(alreadyApplied) return;

    const score = calculateScore(formData, internship);
    setAppliedInternships([
      ...appliedInternships,
      { ...internship, score, status: "Pending" }
    ]);

    // Update applied count
    internship.appliedCount += 1;
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      {/* Header */}
      <header className="bg-blue-900 text-white py-6 px-8 flex justify-between items-center rounded-lg shadow">
        <div>
          <h1 className="text-3xl font-bold">Student Dashboard</h1>
          <p className="mt-1 text-lg">Welcome, {formData.name}! Here's your internship overview.</p>
        </div>
      </header>

      {/* Profile */}
      <section className="mt-6 bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-2xl font-bold mb-2 text-blue-800">Profile</h2>
        <p><strong>Name:</strong> {formData.name}</p>
        <p><strong>College:</strong> {formData.college || "Not Provided"}</p>
        <p><strong>Location:</strong> {formData.location}</p>
        <p><strong>Skills:</strong> {formData.skillsArray.join(", ")}</p>
        <p><strong>Category:</strong> {formData.category}</p>
      </section>

      {/* Available Internships */}
      <section className="mt-6">
        <h2 className="text-2xl font-bold mb-4 text-blue-800">Available Internships</h2>
        <div className="grid md:grid-cols-2 gap-4">
          {internshipsData.map((intern) => (
            <div key={intern.id} className="bg-white p-4 rounded-lg shadow-md border-t-4 border-blue-600 flex flex-col gap-2">
              <h3 className="font-bold text-lg">{intern.title} - {intern.company}</h3>
              <p>Location: {intern.location}</p>
              <p>Skills: {intern.skills.join(", ")}</p>
              <p>Capacity: {intern.capacity} | Applied: {intern.appliedCount}</p>
              <div className="mt-2 w-full h-3 bg-gray-300 rounded-full">
                <div className="h-3 bg-blue-500 rounded-full" style={{width:`${calculateScore(formData, intern)}%`}}></div>
              </div>
              <p className="text-right font-semibold">{calculateScore(formData, intern)}% Match</p>
              <div className="mt-2 flex gap-2">
                <a href={intern.url} target="_blank" rel="noopener noreferrer" className="flex-1 bg-green-400 hover:bg-green-500 text-white py-1 rounded text-center">View</a>
                <button
                  onClick={() => handleApply(intern)}
                  disabled={appliedInternships.some(i => i.id === intern.id)}
                  className={`flex-1 py-1 rounded text-white ${appliedInternships.some(i => i.id === intern.id) ? "bg-gray-400 cursor-not-allowed" : "bg-blue-600 hover:bg-blue-700"}`}
                >
                  {appliedInternships.some(i => i.id === intern.id) ? "Applied" : "Apply"}
                </button>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Applied Internships */}
      <section className="mt-6">
        <h2 className="text-2xl font-bold mb-4 text-blue-800">My Applications ({appliedInternships.length})</h2>
        {appliedInternships.length === 0 ? (
          <p>No applications yet.</p>
        ) : (
          <div className="grid md:grid-cols-2 gap-4">
            {appliedInternships.map((intern) => (
              <div key={intern.id} className="bg-white p-4 rounded-lg shadow-md border-t-4 border-purple-600">
                <h3 className="font-bold text-lg">{intern.title} - {intern.company}</h3>
                <p>Location: {intern.location}</p>
                <p>Skills: {intern.skills.join(", ")}</p>
                <div className="mt-2 w-full h-3 bg-gray-300 rounded-full">
                  <div className="h-3 bg-purple-500 rounded-full" style={{width:`${intern.score}%`}}></div>
                </div>
                <p className="text-right font-semibold">{intern.score}% Match</p>
                <p className="mt-2"><strong>Status:</strong> <span className={`font-bold ${intern.status==="Accepted"?"text-green-600":intern.status==="Rejected"?"text-red-600":"text-yellow-600"}`}>{intern.status}</span></p>
              </div>
            ))}
          </div>
        )}
      </section>
    </div>
  );
}
