import React, { useState } from "react";

// Sample internship data (simulate backend/AI engine)
const internshipsData = [
  {
    title: "ML Intern",
    company: "Innovate AI",
    location: "Bengaluru",
    skills: ["Python", "ML"],
    capacity: 5,
    appliedCount: 0,
  },
  {
    title: "Data Science Intern",
    company: "Data Insights",
    location: "Hyderabad",
    skills: ["Python", "SQL"],
    capacity: 3,
    appliedCount: 0,
  },
  {
    title: "Frontend Developer",
    company: "WebWeavers",
    location: "Remote",
    skills: ["React", "CSS"],
    capacity: 4,
    appliedCount: 0,
  },
  {
    title: "Backend Developer",
    company: "CodeFactory",
    location: "Pune",
    skills: ["Node.js", "SQL"],
    capacity: 2,
    appliedCount: 0,
  },
  {
    title: "AI Research Intern",
    company: "NeuralNet Inc",
    location: "Bengaluru",
    skills: ["Python", "ML", "TensorFlow"],
    capacity: 3,
    appliedCount: 0,
  },
];

const categories = ["General", "OBC", "SC", "ST", "Rural", "Aspirational"];

export default function MatchEnginePage() {
  const [formData, setFormData] = useState({
    name: "",
    contact: "",
    college: "",
    skills: "",
    location: "",
    sector: "",
    category: "",
    resume: null,
    pastParticipation: false,
  });

  const [submitted, setSubmitted] = useState(false);
  const [matchedInternships, setMatchedInternships] = useState([]);

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if (type === "checkbox") setFormData({ ...formData, [name]: checked });
    else if (type === "file") setFormData({ ...formData, [name]: files[0] });
    else setFormData({ ...formData, [name]: value });
  };

  // Calculate smart score based on skills, category, past participation, location, capacity
  const calculateScore = (student, internship) => {
    const studentSkills = student.skills.toLowerCase().split(",").map(s => s.trim());
    const skillMatchCount = internship.skills.filter(skill =>
      studentSkills.includes(skill.toLowerCase())
    ).length;

    let score = skillMatchCount * 20 + Math.random() * 10; // base skill match + randomness

    // Past participation penalty
    if (student.pastParticipation) score -= 10;

    // Location preference bonus
    if (internship.location.toLowerCase() === student.location.toLowerCase()) score += 5;

    // Affirmative action bonus
    if (["SC","ST","OBC","Rural","Aspirational"].includes(student.category)) score += 10;

    // Capacity constraint
    if (internship.appliedCount >= internship.capacity) score -= 100;

    return Math.max(Math.round(score), 0);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const scoredInternships = internshipsData.map(intern => ({
      ...intern,
      score: calculateScore(formData, intern),
    }));

    scoredInternships.sort((a, b) => b.score - a.score);

    // Pick top 3
    const top3 = scoredInternships.slice(0, 3);

    // Increment appliedCount (simulate backend allocation)
    top3.forEach(intern => {
      const index = internshipsData.findIndex(i => i.company === intern.company && i.title === intern.title);
      if (index !== -1) internshipsData[index].appliedCount += 1;
    });

    setMatchedInternships(top3);
    setSubmitted(true);
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-indigo-50 via-purple-50 to-pink-50 p-6 flex flex-col md:flex-row">

      {/* Student Form */}
      <form 
        className="bg-white p-8 rounded-2xl shadow-2xl w-full md:w-1/2 space-y-4"
        onSubmit={handleSubmit}
      >
        <h2 className="text-3xl font-bold text-blue-900 mb-4">Student Details</h2>

        <input type="text" name="name" placeholder="Full Name" value={formData.name} onChange={handleChange} className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-400" required />
        <input type="text" name="contact" placeholder="Contact Info" value={formData.contact} onChange={handleChange} className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-400" required />
        <input type="text" name="college" placeholder="College/University" value={formData.college} onChange={handleChange} className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-400" required />
        <input type="text" name="skills" placeholder="Skills (Python, ML, React, etc.)" value={formData.skills} onChange={handleChange} className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-400" required />
        <input type="text" name="location" placeholder="Preferred Location" value={formData.location} onChange={handleChange} className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-400" required />
        <input type="text" name="sector" placeholder="Sector of Interest" value={formData.sector} onChange={handleChange} className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-400" />

        <select name="category" value={formData.category} onChange={handleChange} required className="w-full border p-3 rounded-lg focus:ring-2 focus:ring-blue-400">
          <option value="">Select Category</option>
          {categories.map(cat => <option key={cat} value={cat}>{cat}</option>)}
        </select>

        <label className="block">
          Resume/CV Upload
          <input type="file" name="resume" onChange={handleChange} className="mt-2 w-full" required />
        </label>

        <label className="flex items-center space-x-2">
          <input type="checkbox" name="pastParticipation" checked={formData.pastParticipation} onChange={handleChange} />
          <span>Past Participation in Internship</span>
        </label>

        <button type="submit" className="w-full py-3 bg-blue-800 hover:bg-blue-900 text-white rounded-lg font-bold text-lg transition duration-300">Submit</button>
      </form>

      {/* Right Panel: Matches */}
      <div className="md:pl-8 mt-6 md:mt-0 w-full md:w-1/2 flex flex-col">
        {!submitted ? (
          <div className="bg-white p-8 rounded-2xl shadow-2xl flex-1 flex flex-col items-center justify-center space-y-4">
            <h2 className="text-3xl font-bold text-gray-700">Your Internship Matches</h2>
            <p className="text-gray-500 text-center">Fill out the form to see your top 3 internship opportunities!</p>
            <div className="w-full mt-4 h-1 bg-gradient-to-r from-purple-400 via-pink-500 to-red-400 rounded-full animate-pulse"></div>
          </div>
        ) : (
          <div className="bg-gray-900 p-8 rounded-2xl shadow-2xl text-white flex-1 space-y-6">
            <h2 className="text-3xl font-bold text-orange-400">Your Top Internship Matches</h2>
            {matchedInternships.map((intern, idx) => (
              <div key={idx} className="p-4 bg-gray-800 rounded-xl shadow-lg hover:scale-105 transform transition">
                <h3 className="text-xl font-bold">{intern.title} - <span className="text-green-400">{intern.company}</span></h3>
                <p className="mt-1">Location: {intern.location}</p>
                <p>Skills Matched: {intern.skills.join(", ")}</p>
                <p className="mt-2 font-semibold text-yellow-400">Score: {intern.score}</p>
              </div>
            ))}
            <div className="mt-4 p-4 bg-gray-700 rounded-lg text-center text-yellow-300 font-semibold">
              Top 3 internships calculated based on skill match, location preference, affirmative action, and fair allocation
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
